css:
@-moz-document url-prefix("https://developers.mercadolibre.com") {
#root-app {
	background-color: #222;
    color: #DDD;

}

.andes-message.andes-message--neutral {
	background-color: #333;
    color: #DDD;

}

td {
	background-color: #333;
    color: #fff;
}

th.andes-table__header {
    background-color: #555 !important;
    color: #fff;
}

.nav-header {
	background-color: #111;
    color: #ddd;
}

nav {
	background-color: #111 !important;
    color: #ddd !important;
}

.nav-footer { 
	background-color: #111 !important;    
    color: #ccc;
}

.nav-footer-language {
	background-color: #111;
}

.nav-footer-language__link {
    color: #fff;
}


.login-btn > span {
    color: #ddd;
}

li {
 	background-color: #111; 
}

.details__message {
    background-color: #333;
}
p {
	color: #BBB !important;
}

li.details__bullets-text {
	background-color: #222;
}

li > a{
    color: #ccc !important;
}

a {
    color: #00ffcc;
}

img {
	mix-blend-mode: multiply;
    background-color: #ccc !important;
}

img:hover {
	mix-blend-mode: soft-light;
    
}

.container-tabs {
	background-color: #333;
    color: #ccc;
}
}
