css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
  domain("kotaku.com.au"),
  domain("gizmodo.com.au"),
  domain("lifehacker.com.au"),
  domain("defamer.com.au")

{

#footer,

#wrapper > a > img,

#fireplace-right,

#fireplace-left,

#fireplace-center,

#ad-leaderboard-top,

#ad-leaderboard-bottom,

#sidebar,

#network-links,

.tags,

.byline > a > img,

#quick-links,

.links,

.flag,

#ad-mid-page,

#ad-advertorial,

#content div.advertisement

{

display: none !important;
}

#content {

float:none!important;width:auto!important;border-right:none!important;padding-right:0px!important
}

#header {margin-top:14px!important}

#content div.excerpt a.thumbnail img 

{border:1px solid #CCC;clear:left;float:left;margin:7px 0px 0px 0!important}

#content div.image-center a.thumbnail {width: auto !important}

#content div.image-center a.thumbnail img {max-width: 220px !important; margin-right: 10px!important;}

#content div.excerpt div.meta{margin-bottom:10px!important;padding-top:0px!important}

body { background:none!important; }

.video { float: left !important; margin-right: 17px!important; }

#wrapper {margin-bottom: 12px !important }

}
