css:
@-moz-document domain("moodle.ovs.org") {
/* Gradient on nav bar, with text whited to stand out against darker gradient */
nav.navbar {
    background: linear-gradient(90deg, rgba(2, 0, 36, 1) 0%, rgba(14, 121, 9, 1) 35%, rgba(0, 212, 255, 1) 100%) !important;
}
nav.navbar a.navbar-brand, nav.navbar div.drawer-toggle {
    color: white;
}

/* Make user avatar bigger in the nav bar */
.usermenu .avatar img {
    width: 70px;
    height: 70px;
    position: relative;
    left: 10px;
}
li .popover-region {
    position: relative;
    top: 15px; 
}

/* Get rid of "Customize this page" bar on Dashboard */
body#page-my-index header#page-header.row {
    display: none !important;
}

/* Customized header and icons for On The Hill RSS feed */
body#page-my-index section[data-block="rss_client"] div.card-body h5.card-title {
    display: none !important;
}
body#page-my-index section[data-block="rss_client"] {
    background-image: url("https://oth.ovs.org/wp-content/uploads/2013/10/cropped-Mast-Head.jpg");
    background-color: transparent;
    background-repeat: no-repeat;
    background-blend-mode: lighten;
    background-origin: content-box;
	background-position: left top;
	background-size: 100% auto;
}
body#page-my-index section[data-block="rss_client"] div.card-body {
    background-color: transparent;
    padding-top: 60px !important;
}
body#page-my-index section div.block-controls {
    display: none;
}
body#page-my-index section[data-block="rss_client"] div.card-body a:link::before {
    content: "ðŸ“• ";
}
body#page-my-index section[data-block="rss_client"] div.card-body a:hover::before, body#page-my-index section[data-block="rss_client"] div.card-body a:active::before {
    content: "ðŸ“– ";
}

/* Can't remember what this does... */
div[data-region="filter"] {
    display: none !important;
}

/* OVS Logo background for class pages header */
#page-header > div > div {
    background-image: linear-gradient(rgba(255, 255, 255, 0.9), rgba(255, 255, 255, 0.9)), url("https://www.ovs.org/wp-content/uploads/2013/07/left-logo.png");
    background-color: transparent;
    background-repeat: no-repeat;
    background-blend-mode: lighten;
    background-origin: content-box;
    background-position: 0px 2px;
}


}
