css:
@-moz-document domain("lectormanga.com"){

iframe{
    display: none;
}

/* Quitar titulos spoiler */
h4.mt-2.text-truncate {
    width: 25%;
}


.pb-5 {
    background:  #333;
    color: white;
}
#chapters > .chapter-list .list-group-item {
    background: #4658ea;
}
.card {
    background: #333;
}
.table-responsive tbody{
    background: #444;
}

.table-light td{
    border-color: #333;
}

.card-footer {
    background-color: #4658ea !important;
}

.text-manga {
    color: white !important;
}

.text-manhwa {
color: rgb(0,255,1) !important;    
}

.form-control[type="text"] {
    background: #444;
    border: 1px solid #222;
    color: #999;
    outline: none;
}

.text-center .form-control {
    background-color: #444;
    border: 1px solid #333;
    color: #999;
}

}
