css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://social.bioware.com/forum") {

.SEPPosts td textarea, td form textarea { 
height:250px !important; 
color: #000 !important;
}

#userinfo_block, .badge, #sidebar, .moderators, div .SEPForumInstanceHeader, .forum_rss_link, .topicItem .subline, .category .subline {
display : none !important;
}

#content {
padding:0px;
text-align:left;
margin-top:0px !important;
margin-left:7px !important;
width:970px !important;
}

div.item_list.wide, div.browse_fields, .content_block, table.SEPForumCategoriesOverview .parentCategoryMiddleLeft, table.SEPPosts {
	background-color: #323232!important;
	background-image: none !important;
	background-repeat: no-repeat !important;
}

div, td {
font-family:tahoma,"Trebuchet MS",arial,serif !important;
font-size:8pt !important;
/* color:#eee !important; */
}

div .roundbox_head > span, div .roundbox_head, div .roundbox_foot > span, div .roundbox_foot, div .content_headline > span, .parentCategoryTopMiddle, .parentCategoryTopRight, .parentCategoryTopLeft, .parentCategoryBottomMiddle, .parentCategoryBottomLeft, .parentCategoryBottomRight {
	background-color: #323232!important;
	background-image: none !important;
}

div .SEPForumTextOutput {
font-family:tahoma,"Trebuchet MS",arial,serif !important;
font-size:10pt !important;
/* color:#fff !important; */
}

.title {
font-weight:normal!important;
color:#f47373;
font-size:9pt !important;
font-family: arial, serif !important;
}

t-able .clearTable .SEPForumAvatar {
display : none !important ;
}

.SEPForumPagination, .SEPForumPagination td {
	padding: 0px !important;
	margin-top: 0px !important;
	margin-right: 0px !important;
	margin-bottom: 3px !important;
	margin-left: 0px !important;
}
.forum_mainactions, .forum_breadcrumb {
	padding: 0px !important;
	margin-top: 6px !important;
	margin-right: 0px !important;
	margin-bottom: 0px !important;
	margin-left: 0px !important;
}

.forum_breadcrumb{
border-bottom: none !important;
font-size: 9pt !important;
}

.forum_mainactions .button {
padding-top : 0 !important;
}

.small_photo {
	height: 30px !important;
	width: 30px !important;
margin-top: 1px !important;
}

div.small_photo{
background-image: none !important;
background: #000 !important;
width:32px !important;
height:32px !important;
padding:0px !important;
text-align:center;}

table.SEPForumTopicsOverview .background1 td {
background:#323232 !important;
}
table.SEPForumTopicsOverview .background2 td {
background:#3a3a3a !important;
}

.topicItem td {
padding-top : 4px !important;
padding-bottom : 4px !important;
}

/* end - version 0.1 */
}
