css:
@-moz-document domain("userstyles.org")
{
  .form-controls label {
    font-weight: 100;
    color: #323232;
}

table.form-controls, .form-controls>tbody>tr>td, textarea {
    width: 100%;
    font-size: 16px;
    border-radius: 7px;
    background: #ffffff;
    border: 1px solid #00000014;
    padding: 5px;
    box-shadow: 0 1px 2px 0 #efefef4d, 0 2px 6px 2px #6a6a6a26;
    color: #2e2e2e;
}

input[type="button"] {
font-size: 16px;
    border-radius: 7px;
    background: #ffffff;
    border: 1px solid #00000014;
    padding: 5px;
    box-shadow: 0 1px 2px 0 #efefef4d, 0 2px 6px 2px #6a6a6a26;
    color: #2e2e2e;
}

input#example-url {
font-size: 16px;
    border-radius: 7px;
    background: #ffffff;
    border: 1px solid #00000014;
    padding: 5px;
    box-shadow: 0 1px 2px 0 #efefef4d, 0 2px 6px 2px #6a6a6a26;
    color: #2e2e2e;
}

input {
font-size: 16px;
    border-radius: 7px;
    background: #ffffff;
    border: 1px solid #00000014;
    padding: 5px;
    box-shadow: 0 1px 2px 0 #efefef4d, 0 2px 6px 2px #6a6a6a26;
    color: #2e2e2e;
}

input[type="submit"] {
    background: #429fff;
    height: 40px;
    width: 70px;
    color: white;
    border-radius: 30px;
    cursor: pointer;
    border: 2px solid;
    cursor: pointer;
}

table {
    border: 2px solid transparent;
    border-radius: 10px;
    background: white;
    box-shadow: 0 1px 2px 0 #3c40434d, 0 2px 6px 2px #3c404326;
}

tr {
    border: 2px solid transparent;
}

a img {
    border: 0;
    padding: 5px;
    border-radius: 20px;
}
img.logo {
    border: 1px solid white;
    border-radius: 10px;
    box-shadow: 0 1px 2px 0 #3c40434d, 0 2px 6px 2px #3c404326;
    background: white;
    cursor: pointer;
}
textarea#comment-input-new {
    border: 1px solid white;
    border-radius: 3px;
    box-shadow: 0 1px 2px 0 #3c40434d, 0 2px 6px 2px #3c404326;
    background: white;
    height: 20px;
}
.comment-content.edit {
    border: 1px solid white;
    border-radius: 10px;
    box-shadow: 0 1px 2px 0 #3c40434d, 0 2px 6px 2px #3c404326;
    background: white;
}
.white_button.no-select {
    height: 35px;
    border-radius: 30px;
    width: 45px;
    background: white;
    border: 2px solid transparent;
    color: #2e3e49;
}
div#preview_image_div {
    background: #ffffff;
    border: 1px solid #ffffff00;
    border-radius: 10px;
    color: black;
    box-shadow: 0 1px 2px 0 #3c40434d, 0 2px 6px 2px #3c404326;
}
.css_button {
    width: 171px;
    height: 43px;
    background-color: #0098D8;
    display: flex;
    flex-direction: row;
    font-size: 14px;
    font-weight: bold;
    align-items: center;
    justify-content: center;
    color: #ffffff;
    margin-top: 30px;
    cursor: pointer;
    box-shadow: 0 6px 16px 0 #50b2f359;
    border-radius: 30px;
}
#archive_style_button {
    border-radius: 30px;
    box-shadow: 0 6px 16px 0 #50b2f359;
    border: solid 1px #c14430;
    font-size: 14px;
    font-weight: 500;
    text-align: center;
    justify-content: center;
    color: #ffffff;
    width: 171px;
    padding-top: 15px;
    padding-bottom: 15px;
    margin-top: 30px;
    cursor: pointer;
    background: #c14430;
}
}
