css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://java.sun.com/docs/books/tutorial/), url-prefix(https://jsr311.dev.java.net/nonav/), url-prefix(https://jersey.dev.java.net/nonav/) {
  body {
    background: #222 !important;
  }
  body, th, td, p, h5, h5 a, h6, h6 a, h5 span, h6 span, h1, h2, h3, h1 span, h2 span, h3 span, h1 a, h2 a, h3 a, h4, h4 span, h4 a {
    color: #aaa !important;
    font-family: Helvetica,Verdana,Arial,sans-serif !important;
    font-size: 12px !important;
  }
  hr {
    color: transparent !important;
    border: 0px solid #333 !important;
    border-top: 1px solid #333 !important;
  }
  a, a:visited {
    color: #369 !important;
    text-decoration: none !important;
  }
  a:active, a:hover {
    color: #369 !important;
    text-decoration: underline !important;
  }
  #TopBar a:visited, #TopBar a:link {
    color: #fff !important;
    text-decoration: none !important;
  }
  #TopBar a:active, #TopBar a:hover {
    background: transparent !important;
    text-decoration: underline !important;
  }
  #TopBar {
    background-position: 80px 0 !important;
    -moz-border-radius: 10px !important;
  }
  #TopBar_tr, #TopBar_tl, #TopBar_br, #TopBar_bl {
    background: none !important;
  }
  #TopBar_right {
    padding-top: 13px !important;
  }
  #LeftBar {
    margin-right: 10px !important;
  }
  #Contents {
    background: #181818 !important;
    -moz-border-radius: 10px !important;
    padding: 5px !important;
  }
  .jsk-PrimaryBackgroundColor {
    background: #111 !important;
  }
  .jsk-PrimaryFontColor {
    color: #C6C6C6 !important;
  }
  .jsk-SecondaryFontColor, .jsk-ThreadWrapper a.jsk-SecondaryFontColor {
    color: #666 !important;
  }
  table, table tr th, table tr td {
    border: 0px solid #333 !important;
  }
  table {
    border-right: 1px solid #333 !important;
    border-bottom: 1px solid #333 !important;
  }
  table tr th, table tr td {
    border-top: 1px solid #333 !important;
    border-left: 1px solid #333 !important;
  }
  table table, table table tr th, table table tr td {
    border: 0px solid #333 !important;
  }
  .TableHeadingColor {
    background: #111 !important;
  }
  .TableRowColor, .NavBarCell1, .NavBarCell2, .NavBarCell1Rev, .NavBarFont1Rev {
    background: #181818 !important;
  }
  .NavBarCell1, .NavBarCell2, .NavBarFont1 {
    color: #aaa !important;
  }
}
