css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("userscripts.org") {

body {background-color: #002727 !important; color: #0FF !important;}
h1 img {display: none;}
h3 {background-color: #033 !important; border: 1px solid #002727 !important;}
a:focus, a:hover {color: #0FF !important;}
a {color: #0075CE !important; text-decoration: underline !important;}
#header {background-color: #002727 !important; border: 2px solid #033 !important;}
  #header h1 {background: url(data:image/png;base64,) no-repeat !important; width:250px !important; height:36px !important;}
  #header a {color: #0FF !important;}
#content {background-color: #002727 !important; border-color: #002727 !important;}
table tr td.inv {background-color: #002727 !important; color: #0FF !important;}
  th.la {border-color: #0FF !important;}
  tr.by-author td, tr.by-author td.author {border-color: #099 !important;}
  tr.by-author td.body {background: #033 !important;}
  th {border-color: #0FF !important;}
div.stats {background-color: #002727 !important; border: 1px solid #033 !important;}
code {background-color: #055 !important; color: #0FF !important;}
pre {background: #099 !important; border: 1px solid #0FF !important;}
p.error {background: #033 !important; border-color: #099 !important;}
.wide_container h1 {color: #0FF !important;}
.pagination {padding: 5px 0 !important;}
#user_password_confirmation {margin-top: 5px !important;}
.sh_javascript, .sh_sourceCode {font-family: inconsolas, courier !important; font-size: 1.1em !important; line-height: 1.5 !important;}
.sh_string {color: #000 !important;}
.sh_symbol {color: #0FF !important;}

}
