css:
@-moz-document url-prefix(http://profiles.delphiforums.com/n/pfx/profile.aspx) {
  /* structure */
  div.ptcWrapper {
    font-family: 'Verdana', 'Arial', 'Helvetica', 'Geneva', 'Swiss',
    sans-serif !important; width:auto !important; max-width:900pt !important;
    font-size:9pt !important}
  div.ptcWrapper a {
    font-weight:400 !important;}
  div.ptcContainer {
    width:auto !important; max-width:900pt !important;
    padding-bottom:0 !important}
  div.ptcProfileDataBlock .ptcField span.ptcValue {
    width:90% !important}
  div.ptcMainContentPanel {
    width:auto !important; max-width:900pt !important;
    border-width:0 !important; font-size:1em !important;}
  div.ptcMyForums {
    float:right !important; width:70% !important; padding:0 0 0 1.5% !important;
    display:block !important; margin:0 !important; font-size:1em !important;}
  div.ptcContent {
    padding:0 !important}
  div.ptcSidebarRight {
    width:28.5% !important; float:left !important; font-size:9pt !important;
    border-right:0 !important; margin:0 !important;}
  div.ptcSidebarRight div {
    background-image:none !important; width:100% !important;}
  div.ptcSidebarRight h2 {
    border:0 !important; margin:0 !important;
    padding:1pt 0 1pt 5pt !important; font-size:9pt !important;
    width:100% !important;}
  div.ptcSidebarRight .ptcComponent {
    width:100% !important; font-size:1em !important; color:#000000 !important}
  div.ptcPanel div.ptcHead {
    padding:0 !important; margin:0 !important}
  .ptcContentTable {
    width:99.9% !important; margin:0 !important; border:0 !important;
    padding:0 !important; font-size:1em !important;}
  .ptcContentTable h3 {
    border:0 !important; padding:0 0 1pt 5pt !important;
    font-size:9pt !important}
  .ptcContentTable tr.ptcHead td {
    padding:1pt 3pt 1pt 3pt !important}
  .ptcContentTable tr.ptcValue td, .ptcContentTable tr.ptcValueAlt td {
    border-style:solid !important; border-width:0 0 1px 0 !important;
    padding:0 0.5em 0 0 !important;}
  .ptcContentTable th {
    padding:0 0.5em 0 0 !important; border:0 !important}
  .ptcContentTable tr td.ptcSelect, .ptcContentTable tr td.ptcVisible {
    padding:0 0.5em !important; border:0 !important; width:2em !important;
    text-align:center !important;}
  .ptcContentTable td.ptcForum, .ptcContentTable td.ptcMessages {
    width:12em !important;}
  .ptcContentTable td.ptcMessages a:first-child {
    white-space:nowrap !important}
  .ptcContentTable input[type="checkbox"] {
    font-size:9pt !important; margin:0.17em !important}
  div.ptcInfo {
    margin:0 !important; padding:0 !important;}
  div.ptcMyForumsFoot {
    border:0 !important; clear:both !important; padding:0 !important}
  div.ptcMyForumsFoot h2 {
    border:0 !important; padding:0 0 1pt 5pt !important;
    font-size:9pt !important; width:100% !important;}
  div.ptcPromos div.ptcBody {
    margin:0.33em !important;}
  div.ptcSidebarLeft {
    display:none !important}
  table[width="995"] {
    width:100% !important;}
  div.ptcMain, div.ptcContent, div.ptcContentFoot,
  div.ptcTabs, div.ptcHeader, div.ptcNavTop, div.ptcBrandTop,
  div.ptcMyForumsPage, div.ptcProfileMenu, div.ptcProfilePage,
  div.ptcProfileContentPanel {
    width:auto !important; max-width:900pt !important}
  div.ptcMyForumsFoot div.ptcPromos div.ptcBody {
    padding:0.5em;}
  div.ptcPageFooter {
    width:auto !important; max-width:900pt !important;
    background-position:50% 0 !important}
  div.ptcContentColumn {
    width:100% !important; max-width:900pt !important;
    display:block !important}
  div.ptcMyForums div.ptcMyForumsFoot div.ptcComponent div.ptcItem {
    height:8.33em !important}
  div.ptcPanel div.ptcBody #dfoptionArea p {
    margin:0.5em 0 !important; padding:0 1em 0 0 !important}
  div.ptcPanel table#popupHeader td {
    margin:0!important; border:0!important; padding:0 1em 0 0 !important;}

  /* colours */
  div.ptcWrapper a {
    color:#000099 !important;}
  div.ptcWrapper a:visited {
    color:#990000 !important;}
  div.ptcWrapper td.ptcMessages a:visited {
    color:#000099 !important;}
  div.ptcWrapper td.ptcMessages a.a0NewMsgs {
    opacity:0.4}
  div.ptcPanel {
    background-color:#FFFFFF !important;}
  div.ptcPanel div.ptcHead, .ptcContentTable tr.ptcHead td,
  .ptcContentTable tr.ptcHead h3 {
    background-color: #283795 !important; color:#FFFFFF !important;}
  div.ptcMyForums {
    background-color:#999999 !important;}
  div.ptcSidebarRight, div.ptcProfilePage, div.ptcSidebarRight div.ptcPanel {
    background-color:#F9F5DF !important}
  .ptcContentTable tr.ptcValue td, .ptcContentTable tr.ptcValueAlt td {
    border-color:#F7F7F7 !important;}
  select#optionsList, select#optionsList option {
    background-color:white !important;}
}
