css:
/* by makondo, 7/08 */
/* This style is based on Mozillazine new forum Brushed style, for which thanks go to jw25 for help with DOMi digging */

@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(theidiotboard.com) {

/* removes items from display */
#store, #footer, #donations, .spaceRow img { display: none !important; }
/* headerbar */#masthead,
/* blue bar at top and bottom of page */#gootop,#goobot,
/* attention notice */#temphead,
/* welcome to new site */#temphead2,
/* sidebar powered by info */#sidebar > DIV:last-child { display: none !important; }
/* sidebar width */#sidebar, .sidebar { width: 12em !important; }
/* mozine link above side bar */
#sidebar>a[href="http://www.mozillazine.org/"] { display: none !important; }
#logo { display: none !important; } 
.headerbar { text-align: center !important; }
.row1 iframe { display: none; }
.gas, .gas_head { display: none; } 
.supportHeader { display: none !important; }

/* remove 'Google custom search' text from the search bars */
.inputbox.search.itsy { background-image: none !important; }

/* fix for kerz' sigs lineup fix */
.postbody { margin-left: 25px !important; }

/* scrolls */
.postbody { overflow: auto !important; }

/* UAgent */
.back2top { color: #A8AEB3 !important; }

/* 'edited' notes */
.notice { border-top-color: #AFAF96 !important; color: #A8A997 !important; }

/* font */
.content { font-size: 13px !important; font-family: Segoe UI, Comic Sans, Verdana, Helvetica, Arial, sans-serif  !important; }

/* posts font */
.postbody .content { color: #D7DDB6 !important; }
/* signature separator */
.signature { border-top: 2px dotted !important;
border-top-color: #6E7277 !important;
color: #ABB1B6 !important; }

/* post profile - left side w/avatar */
.postbody .author { background: transparent !important; }
.postprofile { background: transparent !important;
color: #99A98B !important; }
.postprofile strong { color: #BFBA9C !important; }

/* text and links */
a, b, p, strong, h3, em { color: #B9C6B3 !important; }
a:not([class=fontsize]):hover:not([class=print]) { background: transparent !important;
color: #A4AEB5 !important; }
/* PM preview */ #preview > DIV > DIV > H3:first-child + DIV { color: #C8CDAC !important; }

/* links colors */
a:visited { color: #8E8874 !important; }
.topictitle a { text-decoration: underline !important; }
.topictitle a:visited { text-decoration: none !important; }
.sidebar { background: transparent !important; color: #BFBA9C !important; }

th, 
td:not([class="code"]):not([class="quote"]):not([class="th thCornerL"]):not([class="th thCornerR"]):not([class="th thTop"]), tr, html  
{ padding-top: 0px !important; 
padding-left: 0px !important; 
border: none !important; }
th,
td[class="th thCornerL"], td[class="th thTop"], td[class="th thCornerR"], ul.topiclist, #tabs { background: url("data:image/png;base64,") transparent !important; } 
th,
td[class="th thCornerL"], td[class="th thTop"], td[class="th thCornerR"] { 
padding: 0px 2px 0px 0px !important;
border: none !important;
height: 28px !important;
color: #B2BCAA !important; }

/* forums topics - index page */
ul.forums { background-color: #CFCFCF !important; }
ul.forums dt { color: #B1BBA1 !important; }
ul.topiclist.dt { color: #B1BBA1 !important; }
ul.topiclist li { color: #B1BBA1 !important; }

/* grid */
ul.topiclist dd { border-left-color: #696B70 !important; }
li.row { border-color: #696B70 !important; }
li.header dd, li.header dt { font-weight: bold !important; color: #60666B !important; }
li.header { color: #60697F !important; }
li.header a { color: #6A7060 !important; }
li.header a:hover { color: #C1CFA6 !important; }

/* forums - titles, last poster name */
li.row a.forumtitle { color: #AE9D7B !important; font-family: Segoe UI, Comic Sans, Verdana, Helvetica, Arial, sans-serif  !important; }

/* forums and threads bar */
li.row:hover { background-color: #BFC5C5 !important; }

li.row a { color: #B5BBAE !important; }
li.row a:hover { color: #EAEFCB !important; }
li.row strong { color: #C4CCBC !important; }

/* header and navbar - top of the page*/
.header{ text-align: center !important; }
.headerbar a { color: #C1C8CA !important; }
.headerbar p { color: #C1C8CA !important; }
.headerbar a:hover { color: #C1C8CA !important; }
.navbar a { color: #B2852C !important; }
.navbar strong { color: #C1C8CA !important; }
.navbar a:hover { color: #D69F34 !important; }
.navbar { background: transparent !important; color: #B2BCAA !important; }

/*search buttons */
a.button1, input.button1, input.button3, a.button2, input.button2 { background-image: none !important; background-color: #73796F !important; border: 1px dotted #B3BCAD !important; color: #DEE9CF !important; }

/* sidebar search boxes, top searchbox */
.inputbox.search.itsy, .inputbox.search.tiny, .inputbox.narrow {
-moz-appearance: none !important; 
background: transparent !important; 
border: 1px dotted  #72767C !important;
-moz-border-radius: 8px !important; 
color: #50617F !important;
font-weight: bold !important; }

.inputbox.search.itsy:hover, .inputbox.search.tiny:hover, .inputbox.narrow:hover, .inputbox.search.itsy:focus, .inputbox.search.tiny:focus,.inputbox.narrow:focus
{ -moz-appearance: none !important; 
background-color: #C9CBCD !important; 
color: #25333E !important; 
font-weight: bold !important; }

h3 { border-bottom-color: #565967 !important; }
hr { border-color: #565967 !important; }

.page-body,.postprofile, .post, .panel, .sidebar, ul.topiclist, #navigation a, #tabs a span { 
border: 1px solid !important;
-moz-border-top-colors: #7C7C7C #63666D #878B92 !important;
-moz-border-right-colors: #7C7C7C #94979A #878B92 !important;
-moz-border-bottom-colors: #7C7C7C #94979A #818890 !important;
-moz-border-left-colors: #7C7C7C #63666D #888C95 !important; }

/* quote and code boxes */
blockquote { background-color: #A2A7A2 !important; border-color: #565967 !important; color: #585E63 !important; }
blockquote blockquote { background-color: #838783 !important; border-color: #585E63 !important; color: #585E63 !important; }
.postbody .codebox { background-color: grey !important; }
dl.codebox { background-color: #DFDFDF !important; border-color: #565967 !important; color: #75796D !important; }
dl.codebox a:hover { color: #878A87 !important; }
dl.codebox code { background-color: #BBC1BB !important; color: #5D6A70 !important;} 

/* Quick reply */
.inputbox { background-color: #9FA49F !important; color: #5A656E !important; }

/* ...  User CONTROL PANEL ... */
.panel { color: #BAC0AD !important; }
.panel.bg1, .panel.bg2 { background-color: #D6D7D9 !important; color: #D0D7C2 !important; }
.panel a, .panel p, .panel strong { color: #D7D8C5 !important; }
dt label { color: #BAC0AD !important; }
DD { color: #A9C0D6 !important; }
DT { color: #A9C0D6 !important; }
#cp-main .pm { background-color: #BABFB9 !important; }
#cp-main a:hover { color: #D0D7C2 !important; }
#cp-main .pm a:hover { color: #989B7D !important; }
#topicreview a:hover { color: #AE5700 !important; }
/* left navbox */
#navigation { padding-top: 29px !important; }
#navigation a { border-bottom: 1px dotted !important; color: #BAC0AD !important; font-size: 11px !important; }
#navigation a span:hover { color: #DFE6CF !important; }
/* inbox - message colours menu */
.cp-mini { background: transparent !important;
color: #BAC0AD !important; }
/* second read-message display */
DIV[class="content current"] { color: #A8AE9D !important; }
/* time */
.rightside { color: #798EA5 !important; }

#tabs a span { font-size: 11px !important; }
#tabs a span:hover { color: #7998A9 !important; }

/* pagination */
.pagination, .pagination span a, .pagination span strong {
color: #C1CFB2 !important;
background-color: transparent !important;
border-color: #4B556B !important; }
.pagination span a:visited, .pagination span a:active {
background-color: transparent !important;
color: #999E96 !important;
border: none !important; }
.pagination span a:link { color: #BDBFB1 !important; font-weight: bold !important; }
.pagination span a:hover {
color: #F5F8DF !important;
background-color: #6C7167 !important;
font-weight: bold !important;
text-decoration: underline !important; }

/* ... misc details ... */
span.corners-top { background-image: none !important; }
span.corners-bottom { background-image: none !important; }
span.corners-top span { background-image: none !important; }
span.corners-bottom span { background-image: none !important; } 
#page-body > P:first-child,
#page-body > P:first-child + P { margin-bottom: .5em !important; }
#page-body > P:first-child + H2 + P { display: none !important; }

/* all the white boxes - selections menus */
select { background-color: #7D878F !important; }

/* UCP options, bottom search options */
LABEL { color: #A1B698 !important; }
/* ... fix links spacing in PM's message history field ... */
#viewfolder > FIELDSET:last-child > LABEL { padding-left: 8em !important; }

/* tabs */
#tabs a { background-image: none !important; }
#tabs a span { background: url("data:image/vnd.microsoft.icon;base64,") !important;
border: 2px solid !important; }
/*border-right: 1px solid !important;
border-left: 1px solid !important; */ 

/* change forum background */
body, .forabg, .forumbg, .bg1, .bg2, bg3, li.row, .panel, #navigation a, th, 
td:not([class="code"]):not([class="quote"]):not([class="th thCornerL"]):not([class="th thCornerR"]):not([class="th thTop"]):not([bgcolor^="#"]), tr, html { background: url("data:image/png;base64,") !important; } }
