css:
@-moz-document domain("deutschlandfunk.de") {


@media print,screen and (min-width:64em) {
 .b-section-container,
 .b-footer,
 .b-header .header-wrapper,
 .b-navigation .navigation-sub-inner,
 .b-breaking-news-bar .breaking-news-bar-wrapper,
 .b-common-page,
 .b-style-guide {
   max-width:100vw
 }
}

@media print,screen and (min-width:64em) {
 .u-content-limiter,
 .b-article-details hr,
 .b-article-details .article-details-text,
 .b-article-extended-emphasis,
 .b-section-container.has-centered-limited-content>.content-wrapper,
 .b-contained-teaser-list,
 .b-common-page .page-text {
  max-width:100vw;
	margin:0;
 }
}

@media screen and  (min-width:64em) {
 .b-image.is-over-container-small,
 .b-calendar-button .calendar-wrapper,
 .b-title-image-teaser .title-image-teaser-figure,
 .b-gallery-image,
 .b-thema-teaser-list .thema-teaser-list-container {
  max-width:100vw;
  margin-right:-2rem;
  margin-left:-2rem
 }
}


@media print,screen and (min-width:64em) {
 .b-two-column-teaser .two-column-actions {
  position: absolute;
  top: 2rem;
  left: auto;
  margin: 0;
  right: 0;
}
 }

.contained-teaser-list-item  dl.b-audio-info-details {
  display: grid;
  grid-template-columns: 1fr 1fr 1fr;
  grid-column-gap: 1rem;
  grid-template-rows: 1fr auto;
  grid-auto-flow: column;
}
.contained-teaser-list-item .audio-info-details-item{
  display: contents;
}
.contained-teaser-list-item  dl.b-audio-info-details dt, 
.contained-teaser-list-item  dl.b-audio-info-details dd{
  min-width:0
}

.b-audio-info-details  .audio-info-details-label {
	min-width:0;
}

.b-contained-teaser-list .contained-teaser-list-item {
  margin-bottom:0.5rem
}

.b-two-column-teaser {
	position:relative;
	border-top:.1rem solid #DDD;
	padding-top:0.5rem;
}
.b-two-column-teaser .two-column-teaser-column:first-child {
 margin-bottom:0.5rem
}

.b-teaser-audiotech .teaser-audiotech-headline {
 margin-bottom:.5rem;
}




@media print,screen and (min-width:64em) {
 .b-audio-player {
  padding:0.4rem 0
 }
 .b-audio-player .audio-player-bottom {
  display:flex;
  flex-direction: row;
 }
	
 .b-audio-player > div:nth-child(1) {
  display:flex;
 }
}

.b-audio-player.show-animate-exit, .b-audio-player.show-animate-enter, .b-audio-player.show-animate-enter-done {
  box-shadow: 0 0 2rem #000000;
}


}
