css:
@-moz-document domain("forums.eve-scout.com") {
body.lights-out {
  color: #aaa;
  font-family:'Lato', sans-serif;
  background-image: url(https://cdn.discordapp.com/attachments/839318129716101161/937609915848073256/Star_bg.jpg);
  background-position: top center;
  background-repeat: no-repeat;
  background-color: #000;
  background-size: 100%;
  line-height: 1.5;
}

body.lights-out {
  font-size: 162%;
}
body.lights-out .navbar-brand {
  font-size: 1.6em;
}
body.lights-out .breadcrumb {
  font-size: 1.4em;
}
body.lights-out .topic .post-signature {
  font-size: 0.85em;
}
body.lights-out .topic .threaded-replies {
  font-size: 0.8em;
}
body.lights-out .categories>li .content h2, body.lights-out .category>ul>li .content h2 {
  font-size: 1.4em;
}
body.lights-out .categories .description, body.lights-out .category .description, body.lights-out .subcategory .description {
  font-size: 1em;
}
body.lights-out .h1 small, body.lights-out body.lights-out .h2 .small {
  font-size: 90%;
}
body.lights-out .categories>li .card, body.lights-out .category>ul>li .card {
  font-size: 0.8em;
  line-height: 1.2;
}

@font-face {
  font-family: 'Lato';
  font-style: normal;
  font-weight: 400;
  src: local('Lato'), url(https://fonts.gstatic.com/s/lato/v14/S6uyw4BMUTPHjx4wXiWtFCc.woff2) format('woff2');
}
body.lights-out .navbar-default {
  background: url(https://cdn.discordapp.com/attachments/839318129716101161/937604643373080626/Screenshot_384.png) center no-repeat;
  background-size: 100%;
}
body.lights-out .navbar-default .navbar-brand {
  color: #fff !important;
}
body.lights-out .category li.category-item {
  border-top: none;
}
body.lights-out .mobile-stat {
  color: #aaa;
}
body.lights-out hr {
  border-top: 1px dotted #666;
}
body.lights-out .topic .post-signature {
  border-color: #555;
}
body.lights-out a {
  color: #7cf;
}
body.lights-out .breadcrumb .active {
  color: #aaa;
}
body.lights-out .category>ul>li:not(.unread) h2 a {
  color: #ccc;
}
body.lights-out .category>ul>li:not(.unread) .card {
  border-color: #444 !important;
}
body.lights-out .navbar-default .navbar-nav>.open>a {
  background-color: #555;
}
body.lights-out .dropdown-menu {
  background-color: #444;
  color: white;
}
body.lights-out .posts-list .posts-list-item .topic-title {
  color: #68e;
}
body.lights-out .header .chat-list, body.lights-out .slideout-menu .chat-list {
  color: white;
}
body.lights-out .category .category-item.highlight, body.lights-out .topic .topic-item.highlight {
  background-color: #444;
}
body.lights-out .navbar-default .navbar-brand {
  color: #aaa;
}
body.lights-out pre {
  font-size: 80%;
  line-height: 1.2;
}
body.lights-out .topic .posts .content pre {
  max-height: inherit;
  overflow: visible;
}
body.lights-out .hljs {
  background-color: #777;
  color: #ccc;
}
body.lights-out .hljs-string, body.lights-out .hljs-title, body.lights-out .hljs-section, body.lights-out .hljs-built_in, body.lights-out .hljs-literal, body.lights-out .hljs-type, body.lights-out .hljs-addition, body.lights-out .hljs-tag, body.lights-out .hljs-quote, body.lights-out .hljs-name, body.lights-out .hljs-selector-id, body.lights-out .hljs-selector-class {
  color: #ccc;
}
body.lights-out .categories .description, body.lights-out .category .description, body.lights-out .subcategory .description {
  color: #ccc;
}
body.lights-out .write-preview-container {
  color: #333;
}
body.lights-out .category>ul>li .tag-list .tag {
  background-color:  #7c5353;
}
body.lights-out .table-striped>tbody>tr:nth-of-type(odd) {
  background-color: #282932;
}
body.lights-out .table-bordered>thead>tr>th {
  border-color: #333;
}
body.lights-out .table-bordered {
  border-color: #333;
}
}
