css:
@-moz-document domain("trello.com") {
.window-main-col {
    float: none;
    width: auto;
    padding: 0 16px;
}

.window-sidebar {
    display: -ms-grid;
    display: grid;
    width: auto;
    float: none;
    padding: 0 16px;
    -ms-grid-columns: 1fr 1fr;
        grid-template-columns: 1fr 1fr;
    grid-gap: 15px;
}

/* .window-main-col > .window-module:last-child, */
.js-more-menu,
.js-subscribe,
.js-unsubscribe,
.js-convert-to-template,
.js-copy-card,
.js-move-card,
.window-sidebar hr,
.window-module > h3,
.js-butler-card-buttons,
.js-plugin-buttons,
.js-change-card-members,
.js-edit-labels,
.js-edit-location,
.js-date-range-picker,
.js-attach,
.js-card-cover-chooser,
.js-cf-disabled,
.js-custom-fields,
.js-card-back-custom-fields-prompt,
.js-suggested-actions {
    display: none;
}

.js-add-checklist-menu,
.js-archive-card,
.js-unarchive-card,
.js-delete-card {
    max-width: none;
}

.checklist-item-details-text a {
    font-weight: 600;
    font-style: italic;
    border-bottom: 1px dashed #b9b9b9;
    text-decoration: none;
}

.checklist-item.checklist-item-state-complete {
    background-color: #091e420a;
}
.checklist-item-state-complete .checklist-item-details-text {
    color: inherit;
    text-decoration: none;
}
    
.checklist-items-list {
    counter-reset: checklist;
}
.checklist-item-details-text:before {
    counter-increment: checklist;
    content: counter(checklist) ". ";
}

.card-detail-window {
    max-width: none;
}

.window-overlay > .window {
    width: auto;
    margin: 10px;
}

.checklist-items-list {
    display: -ms-grid;
    display: grid;
        grid-template-columns: repeat(3, 1fr);
}
  
.checklist-item-checkbox {
    top: 2px;
}

.checklist-item-controls {
    position: absolute;
    top: 0;
    right: 0;
    padding: 5px 0px;
    border-bottom-left-radius: 4px;
}
.checklist-item:hover .checklist-item-controls {
    background-color: #f4f5f7;
}

.checklist-item.placeholder {
    position: absolute;
    top: 0;
    left: 0;
    height: 0;
    width: 0;
}
.checklist-item.placeholder + .checklist-item {
    outline: 1px dashed;
}
}
