css:
@-moz-document url-prefix('https://www.platejoy.com/menus/current/') {
@media print {
body { font-family: Campton, 'Helvetica Neue', Arial, sans-serif !important; }
button, form, #chart-wrap, .buttons, .modal, .rating-wrap, .share, .undermain, h2:last-of-type, h2:nth-last-of-type(2) { display: none; }
.filled-horizontal-layout h2 { display: block !important; }
.with-buttons .whisper { color: #999; margin-top: -0.5em; position: relative; text-align: right; top: 1em; }
h1.h2 { font-weight: bold; font-size: 1.75em; }
h1.h2 + p { color: #999; margin-top: -1em; position: relative; text-align: right; }
h2 { font-size: 1.25em !important; font-weight: bold !important; }
table.recipe-ingredients { margin-bottom: 1em; width: 100%; }
table.recipe-ingredients th { text-align: left; }
ul.recipe-steps li.recipe-step > .whisper { color: #666; font-weight: bold; }
ul.recipe-steps li.recipe-step .step-ingredients { display: none; }
}
}
