css:
@-moz-document url-prefix("https://learn.shayhowe.com/") {
:root {
	--gray-900: #202225;
    --gray-800: #292B2F;
    --gray-600: #34373D;
    --gray-550: #3b3e45;
    --gray-500: #dcddde;
    --gray-400: #40444B;
    --border-light: #42454A;
    --border-dark: #202124;
    --primary: #4C58D2;
    --green: #4FDC63;
    --link: #00AFF4;
}

html, .highlight code, .form-toggle, #carbonads a, .bsa-cpc ._default_, .course-overview-lessons a,
h1, h2, b, strong, h1 a, h2 a, .form-label, .tabular-data th {
    color: var(--gray-500);
}

h3, h4, h5, h3 a, h4 a, h5 a, .bsa-cpc .default-title:before, .aside a:not(.btn):hover, .html-css_building-your-first-web-page .building-your-first-web-page a, .html-css_getting-to-know-html .getting-to-know-html a, .html-css_getting-to-know-css .getting-to-know-css a, .html-css_opening-the-box-model .opening-the-box-model a, .html-css_positioning-content .positioning-content a, .html-css_working-with-typography .working-with-typography a, .html-css_setting-backgrounds-and-gradients .setting-backgrounds-and-gradients a, .html-css_creating-lists .creating-lists a, .html-css_adding-media .adding-media a, .html-css_building-forms .building-forms a, .html-css_organizing-data-with-tables .organizing-data-with-tables a, .html-css_writing-your-best-code .writing-your-best-code a, .advanced-html-css_performance-organization .performance-organization a, .advanced-html-css_detailed-css-positioning .detailed-css-positioning a, .advanced-html-css_complex-selectors .complex-selectors a, .advanced-html-css_responsive-web-design .responsive-web-design a, .advanced-html-css_preprocessors .preprocessors a, .advanced-html-css_jquery .jquery a, .advanced-html-css_css-transforms .css-transforms a, .advanced-html-css_transitions-animations .transitions-animations a, .advanced-html-css_feature-support-polyfills .feature-support-polyfills a, .advanced-html-css_semantics-accessibilit .semantics-accessibility a {
    color: var(--link);
}

a:active, a:hover, .form-toggle:active, .form-toggle:hover, #carbonads a:active, #carbonads a:hover, .bsa-cpc ._default_:active, .bsa-cpc ._default_:hover, .course-overview-lessons a:hover, .lesson .lesson-anchor:hover {
    color: var(--link);
}

body {
    background: var(--gray-900);
}

.index .main, .html-css_index .main, .advanced-html-css_index .main {
    background: var(--gray-600) url('https://learn.shayhowe.com/assets/images/triangles/triangles.png') 676px -3px no-repeat;
    background: var(--gray-600) url('https://learn.shayhowe.com/assets/images/triangles/triangles.svg') 676px -3px no-repeat;
    background-size: auto 75%;
}

.main, input, select, textarea, .form-toggles, .media-frame, .lead-recommended-courses, .sg-color-white {
    background: var(--gray-600);
}

@media (min-width: 1670px) {
    .index .main, .html-css_index .main, .advanced-html-css_index .main {
    	background-position: 100% 0;
    }
}

.btn-alt, input:checked+.form-toggle, .sg-color-blue-light {
    background-color: var(--primary);
}

.promo-book, .lead-promo {
    background: var(--gray-900);
    border: 1px solid var(--border-dark);
}

.primary-footer {
    background: var(--gray-800);
}

.box, .note, .form-box, .toc, input, select, textarea, .media-frame, .recommended-courses-empty, .note-stack {
    border-color: var(--border-light);
}

.btn[disabled], .highlight .rouge-gutter, .highlight .lineno:after, .course-overview-lessons a:before, .practice li:before, .sg-color-slate-lighter, .toc-number {
    background-color: var(--gray-400);
}

.rouge-code {
    background: var(--gray-550);
}

.highlight:after {
    background: transparent;
}

p code {
    color: var(--gray-500);
    background: var(--gray-800);
    font-size: 0.8rem;
    padding: 2px 4px;
    border: 1px solid var(--gray-900);
    border-radius: 5px;
}

}
