css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("service.gmx.net") {

#canvas
{
top:0 !important;
}

#navigator, #footer
{
display:none;
}

#nav-sub-bin .end
{
display:none !important;
}

#nav-sub-bin div
{
padding:10px 10px 10px 10px !important;
}


.onlinechat
{
display:none !important;
}
#nav-sub .service .l3 li
{
background-image: none !important;
}
#nav-sub .service .l2 li
{
background-image: none !important;
}

#nav-sub .l3 li a[href*="virus"],
#nav-sub .l3 li a[href*="alias"],
#nav-sub .l3 li a[href*="pushmail"],
#nav-sub .l3 li a[href*="imap"],
#nav-sub .l3 li a[href*="cffilter"],
#nav-sub .l3 li a[href*="options"],
#nav-sub .l3 li a[href*="infoservice"],
#nav-sub .l3 li a[href*="cgi/edcards"]
{
display:none !important;
}

#nav-sub .l2 li a[href*="mobile"],
#nav-sub .l2 li a[href*="products/messenger"],
#nav-sub .l2 li a[href*="g.fcgi/sms?"],
#nav-sub .l2 li a[href*="g.fcgi/fax?"],
#nav-sub .l2 li a[href*="g.fcgi/config/access?"],
#nav-sub .l2 li a[href*="g.fcgi/products/security/landing?"],
#nav-sub .l2 li a[href*="g.fcgi/photo?"],
#nav-sub .l2 li a[href*="g.fcgi/gmxtools?"],
#nav-sub .l2 li a[href*="g.fcgi/entertainment?"],
#nav-sub .l2 li a[href*="g.fcgi/homebanking?"],
#nav-sub .l2 li a[href*="g.fcgi/maildomain/overview?"]
{
display:none !important;
}


#nav-sub .l1 .service
{
background-image: none !important;
}

#nav-sub .service .end {
background-image: none !important;
}
#nav-sub .l1 li a, #nav-sub .l1 li span  {
color: #244E7E  !important;
}


#nav-sub .l2 li[style="border-bottom: 1px solid rgb(97, 111, 137) ! important;"]
{
border: none !important;
}

#nav-sub li[style="border-bottom: 1px solid rgb(97, 111, 137);"]
{
border: none !important;
}


#nav-sub a img[alt="Produkte"]{
display: none !important;
}

#nav-sub a img[alt="Themen"]{
display: none !important;
}

#nav-sub a img[alt="Shopping"]{
display: none !important;
}

#nav-sub a img[alt="Entertainment"]{
display: none !important;
}

ul.mboxmenu li
{
border: none !important;
}
#nav-sub
{
width: 180px !important;
}
ul.mboxmenu li a, li.mboxmenuhead a
{
width: 180px !important;
}
div#main
{
width: 850px  !important;
}
div#content-start
{
margin: 0 0 0 207px !important;
}
#adv
{
padding-top: 0px  !important;
}
}
