css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("vbox7.com") 

{



/* --------- Remove vivit bar ----------- */

object[id^=AdServer],div#ads, iframe, div.vboxFooterTyxo, div.vivitBar , #headerLine {display:none!important;}

/* --------- Main background color ----------- */

body {background:#888888!important;}

/* --------- Links - gledai, kachi video ----------- */

div.vboxHeadLinks a {color:#E6F2FC !important;}
div.vboxHeadLinks a:hover {color:#fff !important;}

.clip {
color:#999999 !important;
}




/* --------- Inner area color ----------- */

div.categoriesBarContainer, div.categoriesBar, div.vboxHeaderTop, div#vboxFooter, div#vboxHeader, div#mainContainer{background:#555555!important;}

/* -------------- Play Page ----------  */

div.clipControls{background:#555555 !important;}
div.comments {background:#555555!important;}
div.comments p#kare-pink a{
		font-size:11px !important;
		color:#666666 !important;
		text-decoration:none !important;
	}
div#comnetsConteiner {background:#555555!important;}
div#comnetsConteiner > div#filter {
			margin:20px 0 0 0!important;
			font-size:10px !important;
			color:#666666 !important;
		}
div#mainContainer table[width="100%"] td[valign="top"] table.kare-grey[width="100%"] {
	background:#444444!important;
	border:0!important;
}
div#mainContainer table[width="100%"] td[valign="top"] table.kare-grey[width="100%"] td[bgcolor="#e2e2e2"] {
			background:none!important;
		}
		div#mainContainer table[width="100%"] td[valign="top"] table.kare-grey[width="100%"] strong{
			color:#999999!important;
			font-weight:normal!important;
		}

div#comnetsConteiner > div#filter strong{font-weight:normal!important;}

/* --------------FOOTER----------  */

#vboxFooter {display:none !important;}

	

/* ---------------Top 40----------*/
div.mainTopContainer {margin-left: -15px!important;}

table.statisticTable tr.trOut td.number, table.statisticTable tr.trOver td.number  {
width:30px!important;
font-size:15px!important;
font-weight:bold!important;
color:#fff!important;
background:#516473!important;
}

table.statisticTable > tbody > tr >td {
background:#516473!important;
color:#fff!important;
}
table.statisticTable a {
color:#fff!important;
}
table.statisticTable a:hover {
color:#E6F2FC!important;
}
table.statisticTable a[href^="/user"] {
font-size:11px ;
}

.StatisticsMenu {
background:#E6F2FC!important;
}

.memberSortSearch {
    background-color: #555555!important;
    margin-bottom: 0px!important;
    margin-top: -25px!important;
}


/* ----------- LOGIN ---------------- */
table[width="700"][height="25"][align="center"] {
	display:none!important;
}
table[width="700"][bgcolor="#cccccc"][align="center"]  {
	background:none!important;
	width:480px!important;
}
table[width="700"][bgcolor="#cccccc"][align="center"] a{
	color:#0865C2!important;
	text-decoration:none!important;
}
table[width="700"][bgcolor="#cccccc"][align="center"] a:hover{
	background:none!important;
	color:#0865C2!important;
	text-decoration:underline!important;
}
table[width="700"][bgcolor="#cccccc"][align="center"] tr[bgcolor="#eeeeee"]{
	background:none!important;
}
table[width="700"][bgcolor="#cccccc"][align="center"] td[bgcolor="#eeeeee"]{
	background:none!important;
}
table[width="700"][bgcolor="#cccccc"][align="center"] table[width="450"][height="30"] td[height="50"][align="center"]{
	text-align:left!important;
}


table[width="700"][bgcolor="#cccccc"][align="center"] td[bgcolor="#ffffff"] p#kare-grey,
table[width="700"][bgcolor="#cccccc"][align="center"] td[bgcolor="#ffffff"] p#kare-pink  {
	text-align:left!important;
	border:0!important;
	font-size:11px!important;
	color:#6e6e6e!important;
}
table[width="700"][bgcolor="#cccccc"][align="center"] input[name="uemail"],
table[width="700"][bgcolor="#cccccc"][align="center"] input#usrbox{
	color:#333333!important;
	font-size:16px!important;
	font-weight:bold!important;
	height:35px!important;
	margin:0!important;
	padding:5px!important;
}
table[width="700"][bgcolor="#cccccc"][align="center"] form h1{
	color:#FF6699!important;
	padding-bottom:10px!important;
}
table[width="700"][bgcolor="#cccccc"][align="center"] table[width="450"] table[width="100%"] tr[bgcolor="#eeeeee"] span[style="color: rgb(255, 0, 0);"]{
	font-size:11px!important;
	color:#6e6e6e!important;
}


/* ----------- Users - all, online, top ---------------- */
	
td.userDetails .userInfo {
    color: #FFFFFF !important;
}

/* ----------- Profile ---------------- */

.userAux {
    background:none!important;
}

}
