css:
@-moz-document domain("trello.com") {
/*////////////////////////////////////
// COLOR PICKERS // www.flatline.tv //
////////////////////////////////////*/
:root {
  --background: #101010;

  --list: #151515;
  --list-text: #fff;
  --card: #202020;
  --card-hover: #303030;
  --card-text: #fff;
  --placeholder: #666;
  --links: #888;
  --links-hover: #fff;
  --tag-size-width: 10px;
  --tag-size-height: 10px;

  --icons: #888;
  --icons-hover: #fff;
  --button: #303030;
  --button-hover: #666;
  --button-complete: #fff;
  --scrollbar: #202020;

  --transition: all .2s ease;
  --border-radius: 5px;
  --border-radius-small: 3px;
  --border-radius-large: 10px;
}

/*//////////////////////////////////////
// THEMED ELEMENTS // www.flatline.tv //
//////////////////////////////////////*/

/* Main */
#trello-root {
  background-color: var(--background) !important;
  background-image: none !important;
}
#board {
  padding-left: 0px !important;
  scroll-snap-type: initial !important;
}

/* Header */
#header {
  background-color: var(--background) !important;
}
#header input {
  background: var(--card);
  width: 150px;
  border: none;
  transition: var(--transition) !important;
}
#header input:focus {
  width: 250px;
  color: var(--list-text);
  transition: var(--transition) !important;
}
#header input::placeholder, #header label * {
  color: var(--placeholder);
}
nav {
  border: none !important;
}
hr {
  background-color: var(--card-hover) !important;
}

/* Lists View */
.list {
  background: var(--list);
  border-radius: var(--border-radius);
}
.list-header textarea, .textarea.mod-list-name {
  color: var(--list-text);
  -webkit-text-fill-color: var(--card-text) !important;
}
.open-card-composer * {
  color: var(--icons) !important;
  transition: var(--transition);
}
.open-card-composer:hover * {
  color: var(--list-text) !important;
}
.list-card {
  background: var(--card);
  border-radius: var(--border-radius-small);
  box-shadow: none;
}
.list-card:hover, .list-card.active-card {
  background: var(--card-hover);
}
.list-card-title {
  color: var(--card-text);
}
.list-card-composer-textarea {
  color: var(--card-text);
}
.list-card-composer-textarea::placeholder {
  color: var(--placeholder);
}
.nch-button--primary {
  background: var(--button) !important;
  color: var(--button-complete) !important;
  transition: var(--transition) !important;
}
.nch-button--primary:hover {
  background: var(--button-hover) !important;
  color: var(--button-complete) !important;
}
.card-label.mod-card-front {
  width: var(--tag-size-width) !important;
  height: var(--tag-size-height) !important;
  min-width: initial !important;
  max-width: initial !important;
  border-radius: 100px;
}

/* Cards View */
.window {
  background: var(--card);
  width: 80%;
  max-width: 750px;
  padding: 20px 40px;
  box-sizing: border-box;
  border-radius: var(--border-radius-large);
}
.window-main-col {
  width: 550px;
  padding: 0px;
}
.window-header {
  margin: 0px;
  padding: 15px 40px 20px 40px;
}
.window-sidebar {
  width: 60px;
  padding: 0px;
}
.window-module {
  color: var(--links);
}
.window h2 + *, .window h3, .checklist-item-details-text {
  color: var(--card-text) !important;
}
.checklist-item-state-complete .checklist-item-details-text {
  color: var(--placeholder) !important;
}
.window a, .window-header-inline-content, .adgMarkdown .markdown a, .adgMarkdown .markeddown a {
  color: var(--links) !important;
  transition: var(--transition);
}
.window a:hover {
  color: var(--links-hover) !important;
}
.window-header-icon {
  left: 0px;
  top: 25px;
  transition: none !important;
}
.window button, .window input, a.button, .nch-button {
  background: var(--button) !important;
  color: var(--placeholder) !important;
  border-radius: var(--border-radius) !important;
  transition: var(--transition) !important;
}
.window button:hover, .window input:hover, a.button:hover, .nch-button:hover {
  background: var(--button-hover) !important;
  color: var(--button-complete) !important;
}
.window input::placeholder {
  color: var(--placeholder) !important;
}
.card-detail-window {
  background: var(--card);
}
.dialog-close-button {
  margin: 15px;
}
.dialog-close-button:hover {
  background: var(--list);
}
.button-link {
  background: var(--button);
  border-radius: var(--border-radius);
  display: flex;
  align-items: center;
  justify-content: center;
}
.button-link:hover {
  background: var(--button-hover);
}
.button-link * {
  padding: 0px !important;
  margin: 0px !important;
}
.button-link span:nth-child(1n+2) {
  display: none;
}
.button-link.negate span {
  color: var(--button-complete) !important;
}
.description {
  background: var(--list) !important;
  box-shadow: none !important;
}
.description::placeholder {
  color: var(--placeholder) !important;
}
.description-content, .description-content blockquote {
  color: var(--card-text);
  border-width: 3px;
  border-color: var(--card-text);
}
.field, .description-fake-text-area, .is-editing {
  background: var(--list) !important;
  color: var(--placeholder) !important;
  border-radius: var(--border-radius) !important;
  border: none !important;
  box-shadow: none !important;
  outline: none !important;
}
.field:hover, .description-fake-text-area:hover {
  background: var(--list) !important;
  color: var(--button-hover) !important;
}
.field:focus, .description-fake-text-area:focus {
  color: var(--card-text) !important;
}
.field::placeholder {
  color: var(--placeholder) !important;
}
.comment-frame, .comment-box, .comment-box-input {
  background: var(--list) !important;
  color: var(--card-text) !important;
  border-radius: var(--border-radius);
  box-shadow: none !important;
}
.comment-box-input::placeholder {
  color: var(--placeholder) !important;
}
.checklist-item {
  display: flex;
  align-items: center;
  justify-content: center;
}
.checklist-item:hover {
  background: var(--card-hover);
}
.checklist-item-details {
  width: 100%;
}
.checklist-item-checkbox {
  background: var(--list);
  padding: 0px !important;
  margin: 0px 0px 0px 6px !important;
  border-radius: var(--border-radius-small);
  box-shadow: none !important;
  top: initial;
}
.checklist-item-checkbox:hover {
  background: var(--button-hover) !important;
  border-radius: var(--border-radius-small);
}
.checklist-item-state-complete .checklist-item-checkbox {
  background: var(--button-hover) !important;
  box-shadow: none !important;
}
.checklist-item-details-text.markeddown {
  color: var(--placeholder);
}
.checklist-progress-percentage {
  color: var(--placeholder);
}
.checklist-progress-bar {
  background: var(--list);
}
.quiet {
  color: var(--placeholder);
}

/* Icons */
.badge, .badge-icon, .badge-text {
  color: var(--icons);
}
.badge.is-complete * {
  color: var(--button-complete) !important;
}
.icon-sm, .icon-md, .icon-lg {
  color: var(--icons) !important;
  transition: var(--transition);
}
.icon-sm:hover, .icon-md:hover, .icon-lg:hover {
  color: var(--icons-hover) !important;
}

/* Scrollbars */
#board.collapsed-workspace-nav::-webkit-scrollbar-button {
  width: 6px !important;
}
.u-fancy-scrollbar::-webkit-scrollbar-thumb {
  background: var(--scrollbar) !important;
  border-radius: 100px !important;
}

/*//////////////////////////////////////
// HIDDEN ELEMENTS // www.flatline.tv //
//////////////////////////////////////*/

/* Main */
#popover-boundary nav {
  display: none !important;
}

/* Header */
.board-header {
  display: none !important;
}
._1uwC1pZb8teBWi._3_c0zHw6-SHzql ._1V65Wl37-Xmq2p {
  display: none !important;
}

/* Lists View */
.card-composer-container .icon-template-card, .list-card-operation {
  display: none !important;
}

/* Cards View */
.checklist-item-controls *, .helper {
  display: none !important;
}
.window-sidebar h3, .js-plugin-buttons, .js-butler-card-buttons, .button-link-container, .button-link.disabled, .js-custom-fields.button-link, .js-assign, .js-due {
  display: none !important;
}
.card-back-card-back-upgrade-prompt-section {
  display: none !important;
}
.card-detail-item.js-card-back-labels-container.u-clearfix {
  display: none !important;
}

/*///////////////////////////////////////
// MEDIA RESPONSIVE // www.flatline.tv //
///////////////////////////////////////*/

@media screen and (max-width: 900px) {
  /* Cards View */
  .window {
    width: 100% !important;
    max-width: none !important;
    border-radius: 0px !important;
  }
  .window-main-col {
    width: 100% !important;
  }
  .window-sidebar {
    display: none !important;
  }
}

@media screen and (max-width: 750px) {
  /* Cards View */
  .window-header {
    margin: 10px 30px !important;
  }
}
}
