css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(consumerist.com){

HTML
{background:#E0FFFF !important}

BODY
{font-family:helvetica,arial,sans-serif !important;
 max-width:960px !important;
 margin-left:auto !important;
 margin-right:auto !important;
 border:1px solid gray !important}

#header
{border:1px solid gray !important;
 border-top:0 !important}

#inner
{width:960px !important;
 border:1px !important;
 background-image:none !important}

.single .entry-title
{text-align:center !important;
 margin-top:-10px !important}

.wp-caption.alignleft
{margin-top:-3px !important}

.entry-content,.entry-content P
{color:black !important;
 font-size:17px !important;
 text-align:justify !important}

.alignright
{margin-top:5px !important}

#wrap,#footer
{background:white !important}

#comments{margin-bottom:0 !important}
#footer{padding-top:0 !important}

.time
{background:none !important;
 padding-left:0 !important}

#content
{width:940px !important;
 padding-left:10px !important;
 padding-right:10px !important}

/* Polls */
.pds-box
{margin-bottom:10px !important}
.pds-input-label
{width:610px !important}
.pds-view-results
{padding-top:0 !important;
 margin-top:-2px;
 margin-left:10px;
 font-size:15px !important;
 font-weight:bold !important}
.pds-return-poll
{padding-top:2px !important;
 margin-left:10px;
 font-size:12px !important}

#subnav,#header .widget-area,
#sidebar>#text-3,#sidebar>#text-4,
#vip-powered-2,#sidebar,.sharedaddy,
.wp-caption-text,.single .post-info,
#header
{display:none !important}}

@-moz-document url(http://consumerist.com/){
#content{width:620px !important}
#inner{background-image:url(http://s1.wp.com/wp-content/themes/premium/modern-news/images/inner-cs.png) !important}
#header,#sidebar{display:block !important}}
