css:
@-moz-document domain("youtube.com"), domain("www.youtube.com") {
.ytp-chapters-container {
    background: rgb(203,224,240);
    background: linear-gradient(90deg, rgba(149,164,39,1) 0%, rgba(177,223,173,1) 50%, rgba(96,95,219,1) 100%);
}

.ytp-title-link, .ytp-chapter-title-content, .ytp-time-display > span  {
    -moz-box-shadow: 0 0 3px 5px rgba(25,25,137,0.42); 
    -webkit-box-shadow: 0 0 3px 5px rgba(25,25,137,0.42); 
	box-shadow: 0 0 3px 5px rgba(25,25,137,0.42);

    border-radius: 6px;
    background: #19198970;

}
}
