css:
@-moz-document domain(mmorpg.org.pl) {

* {

    --bg-light: #202124;

    --display-ads: none !important;

    --bg-medium: #202124;

    --bg-strong: #151516;

    --font: #ddd;

}

#art_bill_top, #sg_bill_top, #sg_bill_srodek_1, #sg_bill_srodek_2, #sekcja_bill_top, #sg_bill_srodek_3, #sg_half_2, #sg_half_1, #sg_bill_bottom, #sekcja_bill_srodek_2, #sekcja_bill_bottom {

    display: none;

}

a[href="/premium"] {

    display:none;

}

body {

    font-family: Verdana, sans-serif;

     color: var(--font) !important;

}

p {
  font-size: 1rem;
   color: var(--font);
}

h4, h5 {

    font-weight: normal;

}

span {

    font-weight: bold;

    color: var(--font);

}

ul li a {

    justify-content: center;

    padding: 1rem 0rem;

/*     border: 1px solid #474d5b; */
	background-color: var(--bg-medium);
    font-weight:bolder;

}

.qc-cmp-cleanslate {

    display: none !important;

}

form {

    font-weight: bold;

    font-size: 0.8rem !important;

}

form label {

    font-size: 0.9rem !important;

}

}
