css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("iwakuroleplay.com")
  {
    /* NEW MEMBERS */
	.username--style30 {
    color: /*[[newUser]]*/;
	}
    
    /* REGULAR MEMBERS */
	.username--style2 {
    color: /*[[regularUser]]*/;
	}
    
    /* INACTIVE MEMBERS */
	.username--style32 {
    color: /*[[inactiveUser]]*/;
	}
    
    /* BANNED MEMBERS */
	.username--style8 {
    color: /*[[bannedUser]]*/;
	}
    
    /* DONATING MEMBERS */
	.username--style14 {
    color: /*[[donor]]*/;
	}
	.userBanner.ribbonDonor {
    background: /*[[donor]]*/;
    border-color: /*[[donor]]*/;
	}
    
    /* INTERNS */
	.username--style16 {
    color: /*[[intern]]*/;
	}
	.userBanner.ribbonIntern {
    background: /*[[intern]]*/;
    border-color: /*[[intern]]*/;
	}
    
    /* ROLEPLAY STAFF */
	.username--style39, .username--style84 {
    color: /*[[roleplay]]*/;
	}
	.userBanner.ribbonRoleplay {
    background: /*[[roleplay]]*/;
    border-color: /*[[roleplay]]*/;
	}
    
    /* SITE SUPPORT STAFF */
	.username--style61, .username--style82 {
    color: /*[[siteSupp]]*/;
	}
	.userBanner.ribbonSupport {
    background: /*[[siteSupp]]*/;
    border-color: /*[[siteSupp]]*/;
    }
    
    /* COMMUNITY STAFF */
	.username--style31, .username--style83 {
    color: /*[[community]]*/;
	}
	.userBanner.ribbonCommunity {
    background: /*[[community]]*/;
    border-color: /*[[community]]*/;
	}
    
    /* SECURITY STAFF */
	.username--style4, .username--style81 {
    font-family: inherit;
    color: /*[[security]]*/;
	}
	.userBanner.ribbonSecurity {
    background: /*[[security]]*/;
    border-color: /*[[security]]*/;
	}
    
    /* ADMINISTRATOR */
	.username--style3 {
    font-family: inherit;
    color: /*[[admin]]*/;
    text-shadow: none;
	}
	.userBanner.ribbonAdmin {
    background: /*[[admin]]*/;
    border-color: /*[[admin]]*/;
    font-family: inherit;
    color: #fff;
	}
  }
