css:
/* AGENT_SHEET */
@namespace url(http://www.w3.org/1999/xhtml);

/* RSDN color: #5597BB */
/* /2: #2A4B5D */
/* /3: #1C323E */
/* /4: #15252E */
/* /5: #111E25 */
/* /6: #0E191F */
/* /7: #0C151A */
/* /8: #0A1217 */
/* /10: #05090B */


@-moz-document domain("rsdn.ru"), url-prefix("http://img.meta.ua/rsdnsearch/") {

* { background-color: #000000; }
td.m, td.ig,td.ii, div.msg-body.m { background-color: #000000!important; }
.o { background-color: #0A1217!important; }

body { color: #666666!important;  background-color: #000000!important; }

a:link    { text-decoration: none; color: #aaaaaa!important; }
a:visited { text-decoration: none; color: #777777!important; }
a#curlink:visited { background: rgb(208, 224, 240)!important; font-weight: bolder!important; }


/* msg editor */
table#tbl.nm, table#tbl.nm * { background-color: #000000!important; }

/* main screen's buttons */
tr[height="17"] * { background-color: #1C323E!important }

/* left frame menu */
ul#ULRoot * { color: #5597BB!important }
div#tree-toolbar, div#tree-toolbar * { background-color: #1C323E!important; }

/* closing in 5 secs */
form#_ctl1 p { color: #5597BB!important }


/* Wiki headings */
h1.pagetitle, h2.separator { color: #5597BB!important }

td,th { 	border-color: #000000; }

/* msg list, tables in msgs */

form table#messageList, form table#grid tr, table.formatter, table th
{
	border-spacing: 1px!important;
}
form table#messageList th, form table#grid tr, table.formatter, table th
{
	background-color: #15252E !important;
}
tr.odd * { background-color: #05090B; }
tr.even * { background-color: #0C151A; }

/* Main msg list, no of replies */
form table#messageList span[style="color:Black;font-weight:normal;"]
{
	color: #666666!important;
}
form table#messageList
{
	background-color: #000000!important;
}
form table#messageList *
{
	border-color: #000000!important;
}
/* Main msg list, "show more" */
form tr.a * { color: #5597BB!important }

form[action^="MsgList"]
{
	color: #888888!important;
}

 /* msg table */
td.i *, td.i
{
	color: #888888!important;
	background-color: #0E191F!important;
}
/* invisible zeros in the msg table */
div.fix font[color="#f4fff4"]
{
	color: #000000!important;
}

tr#previewRow * { border-color: #444444!important; }

/* voting */
form#Form1 table#grid
{
	background-color: #000000!important;
}
form#Form1 table#grid *
{
	border-color: #000000!important;
}
form#Form1 table#grid tr.gi[style="color:Black;background-color:#E0E0E0;"] *, th.poll *
{
	background-color: #15252E!important;
}

/* moderator's banner */
div.mod, div.mod * { background-color: #180000!important; }
div.mod { color: #ff0000!important; }

/*msg version banner */
div.msg-ver, div.msg-ver * { background-color: #FFD8AA!important; }

	td.s, td.s *
{
	color: #888888!important;
	background-color: #1C323E!important;
}

/* text entries */
div#search-form, textarea#messageText, input#subject, input#toID, select#from *, *.nm, textarea#origin, .poll_inp, .rinp, .inp, select, select *, form[name="searchform"] p[align="center"] *
{
	border-color: #5597BB!important;
	background-color: #000000!important;
	color: #888888!important;
}

/* search page */
td[bgcolor="#F5F9FF"], td[bgcolor="#F5F9FF"] * { background-color: #111E25!important; }
td[bgcolor="#EEF0F4"], td[bgcolor="#EEF0F4"] * { background-color: #0C151A!important; }
div.search-preview div.subject, div.search-preview div.subject * { background-color: #15252E!important; }
div.search-preview div.second-header, div.search-preview div.second-header * { background-color: #0C151A!important; }
/* background for icons */
div#tags tr:first-child *
{
	color: #000000!important;
	background-color: #2A4B5D!important;
}

/* main text */
td, td.l, span.value
{
	color: #666666!important;
}

/* msg borders */
td > table[border="0"] { width:100% }
tr#previewCaptionRow * { background-color: #1C323E!important; color: #5597BB!important; }
form#_ctl1 td > table[border="0"], form#_ctl1 td > table[border="0"] td, form#_ctl1 td > table[border="0"] a { background-color: #0E191F !important; }
.fb, td > table[border="0"] td a { color: #5597BB!important; }

/* msg signature */
td.o, td.o *  { background-color: #0A1217!important; color: #5597BB!important;}

blockquote.q, blockquote.q *
{
	background-color: #181000!important;
}

/* quotes (not blockquotes) */
span.lineQuote { color: #008800!important; }

/* code samples */
div.c, div.c * {	background-color: #0A1217!important;  color: #777777!important; }

/* string literals in code */
span.str, .STRING  { color: #bbbb00!important; }

/* links (not in text), buttons, keywords in code */
span.kw, span.KEYWORD, *.nmb, *.fb, a#upload, label[for="nosmile"], label[for="email"], a[href$="Howtoask.xml"], span#msg, select#list
{
	color: #5597BB!important;
}

/* main header */
div#site-toolbar, div#site-toolbar *, div#profile-toolbar, div#profile-toolbar * {	background-color: #1C323E!important; }
	
/* main page */
table.inf > tbody > tr:first-child *,
table.iinf > tbody > tr:first-child *
{
	background-color: #0E191F!important;
}

/* tooltip for msg references */
.cluetip * { color: #5597BB!important; background-color: #0E191F!important; }
.cluetip-outer * { color: #5597BB!important; background-color: #0E191F!important; background:none!important; }

div.ui-dialog { background:none!important;  }
div.ui-dialog-titlebar * { background-color: #5597BB!important;}
div.ui-widget-content { color: #5597BB!important; background-color: #000000!important; }
button.ui-button * { color: #5597BB!important; }

.rinp { background: #05090B!important; }

td.the { background-color: #0E191F!important; }

/* inline youtube objects */
div[class*="youtube"] { background-color: #000000!important; }

/* new profile page */
div#self-descript { background-color: #000000!important; }
.ui-corner-top * { background-color: #000000!important; }
.ui-corner-top.ui-tabs-selected * { background-color: #0E191F!important; }
div#pers-tab td.c { background-color: #000000!important; }
	
	
/*.reply-btn * { fill: #ff0000!important; }*/
div.msg-hdr, div.msg-hdr * { background-color: #15252E!important; }

}



@-moz-document regexp("http://([^/]+\.)?rsdn.ru/article/.*") {
body, table, td, tr { color: #666666;  background-color: #000000!important; }

th, th * { color: #666666;  background-color: #15252E!important; }
}
