css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.youtube.com")
{
/* Fondo - Cambia "url(Aqui pega la url de una imagen)" de abajo y pon la imagen que quieras
-----------------*/
body {
  background-image: url(http://www.weesk.com/wallpaper/jeux-video/final-fantasy/final-fantasy-final-fantasy-jeux-video.jpg) !important;
  color: #fff !important;
}
.watch-wide-mode #watch-this-vid,
.watch-wide-mode #watch-this-vid #watch-player-div  {
 height: 720px !important;
 padding:0 !important;
}
.watch-wide-mode-grey-background #watch-this-vid {
 height: auto !important;
 padding:0 !important;
}
.watch-wide-mode #movie_player {
 height: 720px !important;
 width: 1235px !important;
 margin-left: -139.5px !important;
}
#logo button, .logo img {
visibility: hidden !important;
}
#logo, #smallMastheadLogo, .logo {
background: url("http://img3.imageshack.us/img3/4093/youtube2.png") no-repeat transparent !important;
}
#signup-youtube-logo {
background: url("http://img3.imageshack.us/img3/4093/youtube2.png") no-repeat transparent !important; padding-left:84px !important; width: 0px !important;
}
#smallMastheadLogo {
padding-left:111px !important;
}
[bgcolor="#ffffff"], [bgcolor="#ffffbb"], [style*="background-color: rgb(255, 255, 255)"], #exContentWrapper, #exRelatedDiv, #exUserContentDiv, #exPlaylistDiv, #hpEmbedUnderBlock, .basicBoxes, .basicBoxesOpacity[class], .pagingDiv, #hpContentBlock, .vListBox, .previewBox, .vGridBox, .dBG, .rBG, #vlogBox div, .empty_player_list, .form_fieldsets, .form_fieldset, #ticker, .user-thumb-large, .footerBox, #pageContainer, #manageContent[style*="background"], #watch-customize-embed-div, .video-thumb-medium, #watch-actions-area .watch-more-action-share, .topic_body, .feedmodule-anchor, .feedmodule, .watch-comment-spam, .smallChannelMastheadTable, .file, .google-signup, .promo-movie-unit, #movie-promo-pager {
  background: transparent !important;
}
/* thumbnail pics
-----------------*/
.v220WrapperOuter, .v120WrapperOuter, .v90WrapperOuter, .v180WrapperOuter, .v50WrapperOuter,
.v220WrapperInner, .v120WrapperInner, .v90WrapperInner, .v180WrapperInner, .v50WrapperInner,
[class^="user-thumb-"] {
  border-color: transparent !important;
  background: transparent !important;
  -moz-box-shadow: 3px 3px 10px rgba(0, 0, 0, 0.4) !important;
}
/* Comments
-----------*/
.watch-comment-head, .watch-comment-head-hidden {
  background: transparent !important;
}
/* Bloque de descripcion del video - Cambia "url(Aqui pega la url de una imagen)" de abajo y pon la imagen que quieras
--------------------------*/
#watch-channel-vids-div {
  background: #000 url(http://3.bp.blogspot.com/_1PDMn1xcsME/R2B4QZYb6AI/AAAAAAAAAV4/UXZqPWM3W6Q/s400/cute_cute_cute_cute.jpg) !important;
}
.watch-video-added.post-date {
  color: #000 !important;
}
}
