css:
@-moz-document url(https://rooster.hetmml.nl/) {
     body, #search, #week-selector {
         background-color: #2b2a33 !important;
         color: #fff;
         font-family: "Inter";
    }
     input, .week-wrapper, #schedule center font {
         font-family: "Inter";
    }
     #search {
         box-shadow: none;
    }
     .tooltip, ::before {
         background-color: #38383d !important;
    }
     .tooltip span strong {
         font-weight: 400;
    }
     input {
         background-color: #38383d !important;
         color: #B1B1BD !important;
    }
     input, .tooltip {
         box-shadow: 0 2px 6px rgba(28, 27, 34, 0.5);
         border-radius: 8px !important;
    }
     #week-selector {
         box-shadow: 0 2px 6px rgba(28, 27, 34, 0.5);
    }
     .mdl-menu__outline {
         background-color: #38383d;
         box-shadow: 0 2px 14px rgba(28, 27, 34, 0.5);
    }
     .mdl-menu__item {
         background-color: #38383d;
         color: #fff;
    }
     .mdl-menu__item:hover {
         background-color: #44444a;
    }
     .tooltip::before {
         z-index: 0 !important;
         border-radius: 6px;
         transform: translateY(4px) rotate(45deg) !important;
    }
     .title .text {
         font-family: "Metropolis";
         font-weight: 700 
    }
     #overflow-button {
         color: #B1B1BD !important;
    }
     .autocomplete-wrapper {
         background-color: #38383d;
    }
     li:hover {
         background-color: #44444a;
    }
     #search #overflow-button:focus, #week-selector button:focus, #search .fav:focus {
         background-color: #2b2a33;
    }
     table[rules="all"] {
         border: none;
    }
     font[color="#000000"] {
         color: white;
    }
     #schedule center font[color="#0000FF"] {
         color: #fff;
    }
     td[bgcolor] {
         background: none;
    }
     td font[color="#ff0000"] {
         color: #ff6a75 
    }
}
