@-moz-document url("http://www.tudou.com/") {

	.vli li a {color:#004276 !important;text-decoration:underline !important;}
		.vli li a:hover {color:#0000EE !important;}
	.billboard a {color:#004276 !important;text-decoration:underline !important;}

	.pf_b1, .pf_b2, .pf_b3 {
		background:none !important;
		text-decoration:underline !important;
		width:auto !important;
		margin:0 8px 0 0 !important;
	}

	
	.h {padding:0 !important}
		.h h2 {
			background:none !important;
			width:auto !important;
