			font-size:14px !important;
			text-indent:0 !important;
			height:19px !important;
		}
		.h h2 a {width:31px !important;color:#F60 !important;}
		
	.f {display:none !important;}
		.fc .m .c {padding: 10px 12px !important;}

	
	.m {
		width:660px !important;
		border-right:1px solid #E6D8CF !important;
		padding-right:0 !important;
	}
	.c {
		padding:16px 0 !important;
		border-bottom:1px solid #E6D8CF;	
	}
	
	.b {margin-top:30px !important;}
	
	.tag {
		float:none !important;
		height:auto !important;
		overflow:visible !important;
		padding:0 !important;
		width:auto !important;
	}
		.tag a {text-decoration:none !important;}
		.tag a {color:#666 !important;}
		.tag a:hover {color:#000 !important;}
	
	#adex_box {display:none !important;}
	
	/* recover float */
	.sec2 .sc3,
	.sec6 .sc3
	  {float:none !important;}

	.sec2 .vli {
float:right !important;width:220px !important;
margin-top:-60px !important;
}
		.sec2 .vli li {
			background:none !important;padding-bottom:10px !important;
			float:none !important;
			margin:0 !important;
			overflow:visible !important;
			width:auto !important;
			height:auto !important;
			line-height:1.5em !important;
			padding: 0 0 10px 0 !important;
		}
		
		.vli li a {
			display:inline !important;
			height:auto !important;
			overflow:visible !important;
			padding:0 !important;
		}

	.focus {
		background:none !important;
		float:right !important;
		width:200px !important;
			padding: 0 30px 0 0 !important;
	}
		.focus .pic {
			background:none !important;
			padding:0 !important;
		}
		.focus .txt p {display:none !important;}
	

	#iProf_c {padding:0 !important;border:none !important;}
	#iProf .f {display:none !important;}
	.pf .pic .avatar_xs {margin:0 !important;}
	.pf .f {padding:10px 0 !important;}

	.dw {margin-top:-80px !important;}
	.dw .h {padding-bottom:5px !important;}
	.dw h2 a {height:18px !important;}
	.dw .ap {display:none !important;}
	#dw-livebox {padding:0 !important;border:none !important;}

	.n1,.n2,.n3,.n4,.n5,.n6,.n7,.n8,.n9,.n10 {padding:5px 0 !important;}
	
	/* recover bgimg */
 	.n1,.n2,.n3,.n4,.n5,.n6,.n7,.n8,.n9,.n10,
	.c,.h
	 {background:none !important;}

	.pack .dg_n {
		top:60px !important;
		left:66px !important;
	}
	
	/* recover overflow */
	.dw .c {overflow:hidden !important;}
	.fr,
	.s {overflow:visible !important}
	
	/* hide elements */
	.top4,
	.ap,
	.pack_video_card .info
	{display:none !important}

	/* recover height */
	.h,
	.m .c,
	.pf .c,
	.sec6 .focus .txt,
	.top1 .c,
	.top2 .c,
	.top3 .c
	{height: auto !important;}

}


