css:
@-moz-document url-prefix("https://duckduckgo.com/") {
.logo_homepage  {
    width: 278px;
    height: 231px;
    background-image: url(https://external-content.duckduckgo.com/iu/?u=/*[[ddgimage]]*/) !important;
}

.dark-header .header__logo {
    background-image: url(https://external-content.duckduckgo.com/iu/?u=/*[[logo]]*/);
    width: 57px;
    height: 57px;
    background-size: 57px 57px;
}

.header__logo {
    background-image: url(https://external-content.duckduckgo.com/iu/?u=/*[[logo]]*/);
    width: 57px;
    height: 57px;
    background-size: 57px 57px;
}

.site-wrapper {
	background-image: url(https://external-content.duckduckgo.com/iu/?u=/*[[wpimg]]*/);
    background-size: cover;
    background-repeat: no-repeat;
	background-position: center center;
	background-attachment: fixed;
}

.header-wrap {
	background:rgba(0,0,0,0.8) !important;
}

.page-index {
	background:rgba(0,0,0,0.6) !important;
}

html {
    background-image: url(https://external-content.duckduckgo.com/iu/?u=/*[[wpimg]]*/);
    background-size: cover;
    background-repeat: no-repeat;
	background-position: center center;
}

.content-wrap {
	background:rgba(0,0,0,0.5);
}
}
