css:
@-moz-document domain("bakkesplugins.com") {
:root {
	--mainBackground: #232323;
	--textColor: #fff;
	--hover: #1bb1ff;
	--hoverBackground: #393939;
	--boxBackground: #333;
}

/* Background */
body,
.footer,
.dropdown-content {
	background: var(--mainBackground);
}

.dropdown-content {
	box-shadow: 0px 0px 5px gray;
}

/* Text light color */
body,
p,
strong,
.title,
.content h1,
.content h2,
.content h3,
.content h4,
.content h5,
.content h6,
.breadcrumb li.is-active a,
.tag:not(body).is-dark,
.menu-label,
.card,
.table,
.table thead td,
.table thead th,
.menu-list a,
.label,
.panel-heading,
.panel-block,
.filedrop-container,
.filedrop-container .filedrop-cta small,
.navbar-item,
.navbar-link,
.dropdown-content,
.control .fas,
.plugin-more-dropdown-btn,
.plugin-more-dropdown-btn.is-active,
.button:hover,
.button:focus:not(:active),
.button.is-focused:not(:active),
.navbar-burger {
	color: var(--textColor);
}


/* Links and buttons*/
a,
.breadcrumb a,
.subtitle,
.dropdown-item,
a.navbar-item:focus {
	color: #94bdff;
	font-weight: bold;
}

a:hover,
.breadcrumb a:hover,
.filedrop-container .filedrop-cta:hover,
a.dropdown-item:hover,
.navbar-link:hover,
a.navbar-item:focus-within,
a.navbar-item:hover,
a.navbar-item.is-active,
.navbar-link:focus,
.navbar-link:focus-within,
.navbar-link:hover,
.navbar-link.is-active,
.navbar-dropdown a.navbar-item:focus,
.navbar-dropdown a.navbar-item:hover,
.fas:hover,
.showoff:hover,
a.has-text-danger:hover,
a.has-text-danger:focus {
	color: var(--hover);
}

a.dropdown-item:hover {
	background-color: #333;
}

.button.is-link.is-outlined,
.pagination-previous,
.pagination-next,
.pagination-link {
	background-color: #eee;
}

.button,
.button.is-primary,
.navbar-item.has-dropdown:hover .navbar-link,
a.dropdown-item:hover,
.navbar-link:hover,
a.navbar-item:focus-within,
a.navbar-item:hover,
a.navbar-item.is-active,
.navbar-link:focus,
.navbar-link:focus-within,
.navbar-link:hover,
.navbar-link.is-active,
.navbar-dropdown a.navbar-item:focus,
.navbar-dropdown a.navbar-item:hover,
.filter-radio:hover,
.table.is-hoverable.is-striped tbody tr:not(.is-selected):hover,
.showoff:hover,
.button.is-primary:hover,
.button.is-primary.is-hovered,
.button.is-light:hover {
	background-color: var(--hoverBackground);
	font-weight: bold;
}

/* Tables and boxes */
.table,
.card,
.panel-heading,
.filedrop-container,
.navbar,
.navbar-dropdown,
.navbar-item,
.navbar-menu {
	background-color: var(--boxBackground);
	border-radius: 5px;
}

.panel-heading {
	border-radius: 10px 10px 0 0;
}

.panel {
	border-radius: 6px;
	box-shadow: 0 .5em 1em -0.125em rgba(238, 238, 238, .1), 0 0 0 1px rgba(238, 238, 2382, .02);
	font-size: 1rem;
}

.navbar-dropdown {
	border-top: black;
}

/** Text size fixing **/
.showoff-title,
.showoff-description,
.showoff .showoff-stats {
	font-size: 1vw;
}

.showoff-header {
	font-size: 2vw;
}

.showoff-title {
	height: 1.4vw;
	overflow: hidden;
	text-overflow: ellipsis;
	white-space: nowrap;
	font-weight: bold;
}

@media screen and (max-width: 1215px) {
	.showoff-title,
	.showoff-description,
	.showoff .showoff-stats {
		font-size: 2vw;
		-webkit-line-clamp: 3 !important;
	}
	.showoff-header {
		font-size: 3vw;
	}
	.showoff-title {
		height: 3.2vw;
	}
	.showoff-description {
		height: 9vw;
	}
}

@media screen and (max-width: 768px) {
	.columns .column.showoff-content {
		height: 19vw;
	}
	.showoff {
		border-bottom: 1px solid white;
	}
	.showoff .showoff-stats {
		bottom: 16px;
	}
}
}
