css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("fark.com"), domain("totalfark.com") {

body{
font-family: Arial, Helvetica, Verdana !important;
background-color:#65669E !important;
color: #000000 !important;
font-size:76% !important;
}

#siteContainer {
position:relative !important;
width:99% !important;
margin:0 !important;
clear:both !important;
padding:0 0 160px 0 !important;
min-width:990px !important;
}

.headlineRow:hover {
background: #ffffff !important;
}

#bodyMainContainer {
margin-left:8% !important;
}

.mainDate{
background-color: #e0e0e0!important;
color:#000000!important;
text-align: left !important;
}

#bodyMainContainer {
width: 92% !important;
}

#TFbodyMainContainer
{
margin-left:8% !important;
width: 92% !important;
}

#TFbodyRightSideContainer A
{
color: #F9FF9A !important;
}

#bodyContainer {
margin: -1px 0 0 0 !important;
}

#bodyColorTabs {
margin-top:2px !important;
background:#fff !important;
}

#bodyTabContainer {
width:77% !important;
background:#fff !important;
padding-left:23% !important;
border-right:solid #3d3d3d 2px;
border-left:solid #3d3d3d 2px;
}

#TFbodyTabContainer {
width:70% !important;
background:#fff !important;
padding-left:30% !important;
border-right:solid #3d3d3d 2px;
border-left:solid #3d3d3d 2px;
}

#topMenu {
background:#fff !important;
}

#topMenu ul li a {
color:#000 !important;
}

#topMenu ul li a:visited {
color:#000 !important;
}

#commentsArea BR {
margin: -1px !important;
}

#commentsArea {
width: 100% !important;
text-align: left !important;
}

#commentsArea .ctableTF{
margin: 0px !important;
padding: 4px !important;
padding-left: 175px !important;
width: 100% !important;
border: 0px !important;
background-color: #e0e0e0 !important;
color: #000000 !important;
}

#commentsArea .ctableTF a{
color: #880000 !important;
}

#commentsArea .ctable{
margin: 0px !important;
padding: 4px !important;
padding-left: 175px !important;
width: 100% !important;
border: 0px !important;
background-color: #eeeeee !important;
color: #000000 !important;
}

#commentsArea .ctable a{
color: #880000 !important;
}

#commentsArea .cdate {
margin-left: 8.1% !important;
position: absolute !important;
left: 3px !important;
padding-left: 2px !important;
width: 150px !important;
height: 14px !important;
font-size: 10pt !important;
text-align: left !important;
}

#commentsArea .ctext {
font-family: Verdana, Arial, Helvetica !important;
margin: 0px !important;
padding-top: 13px !important;
padding-bottom: 6px !important;
padding-left: 5px !important;
padding-right: 5px !important;
width: 99% !important;
text-align: left !important;
}



#Mcontainer {
background:#ffffff url(https://ssl.fark.com/images/2007/site/bodyGradientNotNews.gif) top left repeat-x !important;
}

.bodyHeadlineContainerNotNews, .bodyHeadlineContainerVoting, .bodyHeadlineContainerFoobies {
background:#ffffff url(http://img.fark.com/images/2007/site/bodyGradientNotNews.gif) top left repeat-x !important;
}

.bodyHeadlineContainerGeek {
background:#ffffff url(http://img.fark.com/images/2007/site/bodyGradientGeek.gif) top left repeat-x !important;
}

.bodyHeadlineContainerSports {
background:#ffffff url(http://img.fark.com/images/2007/site/bodyGradientSports.gif) top left repeat-x !important;
}

.bodyHeadlineContainerPolitics {
background:#ffffff url(http://img.fark.com/images/2007/site/bodyGradientPolitics.gif) top left repeat-x !important;
}

.bodyHeadlineContainerShowbiz {
background:#ffffff url(http://img.fark.com/images/2007/site/bodyGradientShowbiz.gif) top left repeat-x !important;
}

.bodyHeadlineContainerVideo {
background:#ffffff url(http://img.fark.com/images/2007/site/bodyGradientVideo.gif) top left repeat-x !important;
}

.bodyHeadlineContainerMusic {
background:#ffffff url(http://img.fark.com/images/2007/site/bodyGradientMusic.gif) top left repeat-x !important;
}

.bodyHeadlineContainerTFDisc, .bodyHeadlineContainerTFAll, .bodyHeadlineContainerTFCom, .bodyHeadlineContainerTFVoting {
background:#ffffff url(http://img.fark.com/images/2007/site/bodyGradientTFAll.gif) top left repeat-x !important;
}

}
