css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wikipedia.org") {
body,h1, h2, h3, h4, h5 {
    background:#000 !important;
    font-family:"Microsoft YaHei" !important;
}
h1, h2, h3, h4, h5, h6, p{
   color:#080 !important;

}
#content {
    background:#000 !important;
    color:#080 !important;


}
div, table, small, tbody, td {
    font-family:"Microsoft YaHei" !important;
    background:#000 !important;
    color:#290 !important;
}
pre {
    background:#000 !important;
    color:#290 !important;
}
div {
    font-size: 1.1em !important;
}
a:hover {
    color:#f00 !important;
    background:#000 !important;
    border: none !important;

}
li a {
   background:#000 !important;
 
}
th {
   background:#207 !important;
}
div[class~="thumb"][class~="tleft"] {
    background:#000 !important;
    border-top: 8px solid #000 !important;
    border-right: 22px solid #000 !important;
    border-bottom: 12px solid #000 !important;
    border-left: 0px solid #000 !important;
}
div[class~="thumb"][class~="tright"] {
    background:#000 !important;
    border-top: 8px solid #000 !important;
    border-right: 0px solid #000 !important;
    border-bottom: 12px solid #000 !important;
    border-left: 22px solid #000 !important;
}
}
