css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("twitter.com"){
  #header{margin-top:16px!important;}
  #container{width:960px!important; margin:2px!important; left:16px!important; position:absolute!important;}
  .content-bubble-arrow{visibility:hidden!important;}
  #content{background-color:transparent!important; width:784px!important; position:absolute!important; left:176px!important; -moz-border-radius:8px!important; overflow:hidden!important;}
  #side_base{opacity:0.6!important; width:160px!important; z-index:0!important; position:absolute!important; left:0!important; -moz-border-radius:0!important; overflow:hidden!important;}
  #side_base:hover{opacity:0.9!important}
  #side_ad_base{display:none!important}
  #side{width:99%!important;}
  .wrapper,.section,ol,li,.sidebar-menu li a{padding:1px!important; margin:0px!important;}
  .status-body{width:85%!important;}
  .entry-content{font-weight:bold!important;}
  ol#timeline li{opacity:0.8!important}
  ol#timeline li:hover{opacity:1.0!important}
  #status_update_box{opacity:1.0!important; position:fixed!important; right:16px; bottom:64px; width:540px!important; z-index:10!important; background-color:#FFF; -moz-border-radius:4px; border:1px solid #888!important;}
  #status_update_box:hover{opacity:1.0!important;}
  #footer{clear:both!important; overflow:hidden!important;}
  #side .promotion{width:160px!important; padding:1px!important;}
  #sidebar_search input{width:100px!important;}
  #heading,#home_tab,#footer{display:none!important;}
  #timeline li{height:48px!important;}
  #timeline li.latest-status{height:80px!important;}
  ul.actions-hover{bottom:0!important; right:24px!important; width:24px!important;}
  span.entry-content,span.meta{display:inline!important;}
  li#bio{width:160px!important;}
  #following_list a[rel=contact]{display:block!important; text-decoration:none!important; margin:0!important;}
  #following_list a[rel=contact] img{margin:0 8px -4px 0!important; text-decoration:none!important; border:1px solid #999!important;}
  #following_list a[rel=contact]:after{content:attr(title)!important; font-family:Verdana!important; font-size:.9em!important;}
  #following_list a[rel=contact]:hover{text-decoration:underline!important}
}
