/* Greasemonkey */
menu#gm_general_menu {
	-moz-image-region: rect(0px 16px 16px 0px) !important;
}

/* Install User Script */
menuitem#userscript-tools-install {
	-moz-image-region: rect(0px 80px 16px 64px) !important;
}

/* Manage User Scripts */
menuitem[oncommand="GM_BrowserUI.manageMenuItemClicked(event);"] {
	-moz-image-region: rect(0px 32px 16px 16px) !important;
}

/* New User Script */
menuitem[oncommand="GM_BrowserUI.newUserScript();"] {
	-moz-image-region: rect(0px 48px 16px 32px) !important;
}

/* User Script Commands */
menu#userscript-commands-sb,
menu#userscript-commands-sb2,
menu#userscript-commands-sb menuitem[oncommand],
menu#userscript-commands-sb2 menuitem[oncommand] {
	-moz-image-region: rect(0px 64px 16px 48px) !important;
}

/* View User Script Source */
menuitem#view-userscript {
	-moz-image-region: rect(0px 96px 16px 80px) !important;
}

/* No Scripts Installed */
menuitem#gm-status-no-scripts {
	list-style-image: url("chrome://greasemonkey/content/icon_small_disabled.png") !important;
}
