css:
  /* width */
::-webkit-scrollbar {
  width: 5px;
  max-height: 3px;
}

/* Track */
::-webkit-scrollbar-track {
  box-shadow: inset 0 0 3px lightgrey; 
  border-radius: 7px;
}
 
/* Handle */
::-webkit-scrollbar-thumb {
	/*     background: #0000006e; */
    background: lightgrey;
    border-radius: 10px;
}

/* Handle on hover */
::-webkit-scrollbar-thumb:hover {
  background: black; 
  box-shadow: inset 0 0 2px brown; 
}
