css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("kyon.pl") {

/* Site BG and colors */
    body{
	background-color: #3F3F3F !important;
	color: #6495ED !important;
	background-image: url(data:image/gif;base64,R0lGODlhBAAEAIAAADI6ORoaGiH5BAAAAAAALAAAAAAEAAQAAAIGTACGqBkFADs=);
    }
    
    .s {
	color: #D1D1D1 !important;
    }

/*Input boxes and 'add' 'search' buttons*/
/*boxes*/
    input, textarea {
	-moz-appearance: none !important;
	/* otherwise can't change bacground or border on ff*/
	background: white;
	border: 1px solid #114 !important;
	-moz-border-radius: 4px;
	-webkit-border-radius: 4px;
	border-radius: 4px;
	padding: 2px 3px !important;
    }

    input.s, textarea {
	color: #D1d1d1 !important;
	background: #3a3a3a !important;
	border-style: inset !important;
    }
    input.input {
	background:#2a2a2a;
	color:#aaaaaa;
	border-color:#666!important;
	margin:2px;
    }
	a.s[href="#komentarz"]{
	color: #9ac0cd !important;
    background-color: rgba(100, 149, 237, 0.1) !important;
	border: 1px solid #336 !important;
	-moz-border-radius: 4px;
	-webkit-border-radius: 4px;
	border-radius: 4px;
	padding: 0.2em .5em !important;
	margin: 1em !important;
	}
	
/*buttons*/
    input.but, input.u_s {
	background: #333 !important;
	color: #7DAACE !important;
	border-style: outset !important;
    }

/* Top menus */
    div#menu_right a{
	color: #7DAACE !important;
	background-color: rgba(70, 130, 180, 0.2) !important;
	border: 1px solid #114 !important;
	-moz-border-radius: 5px;
	-webkit-border-radius: 5px;
	border-radius: 5px;
    }

    div#menu_right a:hover{
	background-color: rgba(78, 138, 188, 0.38) !important;
    }

    div#menu_right {
	color: #4682b4 !important;
    }

    td b{
	color: #7DAACE !important;
    }

/* Page buttons */
    div#paginator a {
	border:1px solid #114!important;
	background-color:rgba(70,130,180,0.1)!important;
	-moz-border-radius: 3px;
	-webkit-border-radius: 3px;
	border-radius: 3px;
    }
    div#paginator a:hover {
	background-color:rgba(80,150,200,0.15)!important;
    }
    div#paginator span {
	background-color:rgba(80,150,200,0.15);
	border:1px solid #226!important;
	-moz-border-radius: 3px;
	-webkit-border-radius: 3px;
	border-radius: 3px;
    }
/* Tag boxes */
    td.s {
	border-color: #446 !important;
	-moz-border-radius: 10px;
	-webkit-border-radius: 10px;
	border-radius: 10px;
    }

    div#paginator1 a.s, div#paginator2 a.s, div#paginator3 a.s, div#tagcloud a.s {
	color: #9ac0cd !important;
    background-color: rgba(100, 149, 237, 0.1) !important;
	border: 1px solid #114 !important;
	-moz-border-radius: 5px;
	-webkit-border-radius: 5px;
	border-radius: 5px;
	padding: 0.2em .5em !important;
	margin: 0em .5em !important;
    }

    div#paginator3, div#paginator2 {
	text-align: center !important;
	line-height: 2.5em !important;
    }

    div#paginator1 a.s:hover, div#paginator2 a.s:hover, div#paginator3 a.s:hover,
    div#tagcloud a.s:hover {
    background-color: rgba(100, 149, 237, 0.4) !important;
    }

/* tag with single entry */
    div#paginator2 .s[style="background: none repeat scroll 0% 0% rgb(0, 0, 0); color: rgb(255, 255, 255);"] {
	background: rgba(255, 0, 0, 0.2) !important;
    }

    a.x {
	color: #E33 !important;
    }

    a, a.s {
	color: #668292 !important;
    }

/* Comments */
    div#komentarze div#content {
	padding: 1em !important;
    }
	div#komentarz{
	margin: 1em !important;
	}
	input.u_s {
	margin:.5em !important;
	}
	/* green-text?! what green-text? */
	span.cite{
	color: #9f79ee !important;
	}
    /* SPOILERS! */
    .spoiler {
	background:black !important;
	color:black !important;
    }
    .spoiler:hover {
	background:transparent!important;
	color:#d1d1d1!important;
    }
    .spoiler a {
	color: #000 !important;
    }
    .spoiler:hover a{
	color: #668292 !important;
    }

/* popup changes */
    div#overDiv td table:first-child {
	background: #3f3f6f !important;
    }

    div#overDiv td table:first-child b {
	color: #e1e1e1 !important;
    }

    div#overDiv td table:last-child {
	background: #3f3f3f !important;
    }    

    div#overDiv td table:last-child font {
	color: #d1d1d1 !important;	
    }

    div#overDiv td table:last-child b {
	color: #4682b4 !important;
    }
/* Width fix */
    table[width="750"] {
	width: 70% !important;
	min-width: 780px !important;
    }

/* Error msg*/
    div.box{
	background: rgba(75,55,55,0.55) !important;
	border-color: rgba(255,255,255,0.2) !important;
	width: 50% !important;
	margin: 20% 20% !important;
    }
    div.infos{
	color: #d1d1d1 !important;
	background: rgba(10,10,10,0.4) !important;
	border-color: rgba(255,255,255,0.1) !important;
    }
    div.name, div.error{
	background: rgba(250,30,20,0.25) !important;
	border-color: rgba(255,0,0,0.1) !important;
    }

}
