css:
@-moz-document domain("nitrotype.com") {
.dash-copyContainer
    {
      background:url(https://cdn.discordapp.com/attachments/727592912728359054/799375395170418717/Untitled_design_23.png);
      -webkit-box-shadow: 2px 1px 157px 10px rgb(255, 0, 0);
      box-shadow: 0px 1px 157px 10px rgb(0, 179, 255);
      border-radius: 5px;
      flex: 1;
      overflow: hidden;
      padding: 15px;
      display: flex;
    }

    .dash-copy
    {
      color: #0ff;
      text-shadow: 0 0 5px #00ffec, 0 0 7px #0059ff;
      font-family: "Roboto Mono", "Courier New", Courier, "Lucida Sans Typewriter", "Lucida Typewriter", monospace;
      font-size: 36px;

    }
.profile-carContainer.card.card--a.card--shadow-l {background-color:#fff0;}
.raceResults-awards {background:#fff0;}
.raceResults-close.raceResults-close--minimizer {background:#dfd612;}
.g.raceResults-grid {background:#fff0;}
.dash-content {background:url("https://wallpapercave.com/wp/wp2344527.jpg");}


body
  {
    overflow-y: scroll;
    background: none;
    overflow-x: hidden;
    position: relative;
  }

  .card--grit
  {
    background-image: url("https://wallpaperaccess.com/full/199378.jpg");
    background: fixed;
  }

  html
  {
    background: fixed url("https://wallpaperaccess.com/full/199378.jpg") no-repeat, #2d2e2e;
    box-sizing: border-box;
    color: #2d2e2e;
    font-family: "Montserrat", sans-serif;
    font-size: 16px;
    font-weight: 300;
    line-height: 1.6;
    background-size: 100%;
  }

 .bg--gradient {
    background: fixed;
}
.theme--pDefault .profile-bg
    {
      background: url("https://wallpapercave.com/wp/wp2344527.jpg") top left repeat, linear-gradient(0deg, #2d2e2e 60%, #ffffff 100%);
      color: #d3d3d3;
      background-size: 100%
    }

    .theme--pDefault .profile-bgHead
    {
      background: url("https://wallpapercave.com/wp/wp2344527.jpg") top left repeat, linear-gradient(0deg, #2d2e2e 10%, #ffffff 100%);
      background-size: 100%
    }

    .theme--pDefault.is-gold .profile-bg
    {
      background: url("https://wallpapercave.com/wp/wp2344527.jpg") top left repeat, linear-gradient(0deg, #2d2e2e 10%, #f00 100%);
      background-size: 100%
    }
.garage
{
    background:url(https://wallpaperplay.com/walls/full/6/c/d/67928.jpg)
}

path:first-child, path:nth-child(7) {
    fill: #11dfea;
}
}

