css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("minnesotagreyhounds.wordpress.com") {

body { 
font-family: "Lucida Grande", "Lucida Sans Unicode", Helvetica, Tahoma, sans-serif !important; 
color: #575757 !important; 
margin-top: 50px !important; }

/* post and sidebar background color */
div#page, 
div#content {  background: #fefcfa !important;   }


h1,h2,h3 {font-family:  "Palatino Linotype", "Book Antiqua", Georgia, serif !important; 
font-weight: 400 !important; } 

h2 {font-size: 21px !important; line-height: 24px !important; }
 
small {font-size:1em !important;  }

li.comment {  font-size:1.4em !important; }


/* eliminate  outlines on link hover */
a, a:hover { outline:none !important; }

/* avatar code */
.avatar{ float:left !important;  margin-left:0 !important;  }
.avatar-32 {width:40px !important; height: 40px !important; padding: 0 !important; }
div.comment-author p { margin-left: 60px !important; text-align: left !important; }


/* the three br or line-breaks used to create space between body and
page div are nullified and replaced by adding margin-top top 50px to body
to acheive the desired spacial separation of body and page div. 
br continues to provide desired line-break effect elswhere, for example,
in footer */

br {line-height:0 !important; }


/* blog title and description */

#header { 
font-family: "Palatino Linotype", Georgia, serif !important;
line-height:1 !important;
font-weight:normal !important;
}

/* allow previously hidden blog title and description to be visible */
#header * { display: block !important; }

#header h1 {
float:left !important;
position: relative !important;
top: -29px !important;
left: 1px !important;
margin:0 !important;
padding:0 !important;
font-size: 32px !important;
font-weight:bold !important;
font-variant: small-caps !important;
letter-spacing: 0.5px !important;
}

#header h1 a {
color:#BEDAE9  !important;
text-shadow: 1px 1px 1px rgba(190,218,233,0.8) !important; 
}

#header h1 a:hover { 
color: #afd775 !important;
text-shadow: 1px 1px 1px rgba(175,215,117,0.8) !important;
}

#header div.description {
float:right !important;
position: relative !important;
top: -20px !important;
left: -1px !important;
font-size: 21px !important;
color: #ccc !important;
text-shadow: 0 1px 1px rgba(190,218,233,0.8) !important;
font-style: italic !important;
}



/* post container -- left column */
#content{
   padding:20px 0 0 20px !important;
   width:470px !important;
   overflow:hidden !important;
   } 

#content div.navigation div.alignleft,
#content div.navigation div.alignright { 
font-size:1.4em !important; margin-bottom: 10px !important; }

.post{
   font-size:1.4em !important;
   line-height:1.5em !important;
   padding-right:10px !important;
/* text-shadow: 0 0em 0.1em rgba(255,255,255,.90)!important; */
   } 

.post {text-align:left !important; 
}

.post p { text-align:justify !important;  }

p.postmetadata{
   border-bottom: 1px solid #454C67 !important;
   border-top:none !important;
   margin: 25px 0px 25px 0px !important;
   padding-bottom:5px !important;
   padding-top:5px;
   text-align:left !important;
   } 

p.postmetadata  strong {display:none !important; }
p.postmetadata  strong + a:link { display:block !important; }



/* image captions */
.wp-caption {
   background:#eeeeff !important; 
   margin-left:0 !important; 
   margin-right:0 !important;
   }

p.wp-caption-text {
   font-size: 1em !important;
   line-height:21px !important; 
   height:21px !important; 
   padding-bottom:0 !important;
   text-align: center !important;
   height:auto !important;
   }


/* image frames */
img.alignnone {
   margin:0 auto !important;
   padding: 4px !important; 
   background:#eeeeff !important;
   border: 1px solid #ddd !important; 
   -webkit-border-radius: 3px !important;
   -moz-border-radius: 3px !important; 
   -khtml-border-radius:3px !important; 
   }

/* image links -- styled to look like links */
a img.alignnone {border: 1px solid #0040B6 !important;  }
a:hover img.alignnone {border: 1px solid #D2D2FF !important; background:#D2D2FF !important; }



/* right column -- navigation, tag cloud, contact info */
#sidebar{
   margin:20px 0 0 503px !important;
   padding:0 0 0 15px !important;
   width:165px !important;
   border: 1px solid #dddddd  !important;
   background:#eeeeff !important;
   -webkit-border-radius: 3px !important;
   -moz-border-radius: 3px !important; 
   -khtml-border-radius:3px !important;
   } 


#sidebar li {font-size: 1em !important; }
#sidebar ul ul li:before{content:"" !important;} 
#sidebar ul {list-style-position: inside  !important;}


#sidebar ul li.widget_pages,
#sidebar ul li.widget_archive,
#sidebar ul li.widget_links,
#sidebar ul li.widget_text {
   color: #36769c  !important;
   font-size: 1.4em !important; 
   }


#sidebar ul {list-style-position: inside  !important;}

#sidebar ul li.widget_pages a,
#sidebar ul li.widget_archive a,
#sidebar ul li.widget_links a,
#sidebar ul li.widget_text a { color: #36769c  !important; }

#sidebar ul li.widget_pages a:hover,
#sidebar ul li.widget_archive a:hover,
#sidebar ul li.widget_links a:hover,
#sidebar ul li.widget_text a:hover {
   background: #0040b6 !important; 
   color: #fff !important;
   -webkit-border-radius: 3px !important;
   -moz-border-radius: 3px !important; 
   -khtml-border-radius:3px !important;
   }



#sidebar ul li.widget_tag_cloud  a {color: #0040b6 !important;}

#sidebar ul li.widget_tag_cloud  a:hover {
   background: #0040b6 !important; 
   color: #fff !important;
   -webkit-border-radius: 3px !important;
   -moz-border-radius: 3px !important; 
   -khtml-border-radius:3px !important; 
   }


#sidebar ul li.widget_pages a:hover,
#sidebar ul li.widget_archive a:hover,
#sidebar ul li.widget_links a:hover,
#sidebar ul li.widget_text a:hover,
#sidebar ul li.widget_tag_cloud  a:hover {
   background: #0040b6 !important; 
   color: #fff !important;
   padding-left: 1px !important;
   padding-right: 1px !important;
   -webkit-border-radius: 3px !important;
   -moz-border-radius: 3px !important; 
   -khtml-border-radius:3px !important;
   }




/* control wordpress automatic links to possibly related posts -- eliminate outside links */

div.possibly-related  ul {list-style-type: none !important; } 
div.possibly-related  li a[rel~=nofollow]{display: none !important; }




}
