@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://www.youtube.com/")
{

/*Delete these things.. Some temporary deleted, but most of them permanently*/
#toolbar_preload, #promos, #side-announcement-box, #ad_creative_2, .search,
img[class="master-sprite zippy-arrow"], #homepage-whats-new-block,
.links, #statmodules_inbox-title, #ppv-placeholder
{

   display: none !important;

}
/*Move this one down for now*/
#baseDiv
{

   /*padding-top: 600px !important;*/

   background: url('http://i46.tinypic.com/2dgswpi.png') no-repeat !important;

   position: absolute !important;

   left: 0px !important;
   top: 180px !important;

   padding-left: 160px !important;

   padding-right: 100px !important;/*100px*/

   overflow-y: hidden !important;
   overflow-x: hidden !important;
   height: 62.5% !important;

   padding-bottom: 15% !important;

   z-index: 0 !important;




}


body
{

   background: url('http://i256.photobucket.com/albums/hh168/Q9R42/Cosmos_wallpaper_by_Maxtorade.jpg') fixed no-repeat center !important;
   overflow: hidden !important;

}

#masthead-container
{

   border: 0 !important;
   margin-left: 0px !important;
   width: 100% !important;

   background: url('http://i256.photobucket.com/albums/hh168/Q9R42/Header2.png') no-repeat !important;
   height: 210px !important;

}
#logo
{

   background: url('http://i256.photobucket.com/albums/hh168/Q9R42/Logo-4.png') no-repeat !important;
   width: 400px !important;
   height: 120px !important;

  /* margin-top: 40px !important;
   margin-left: 29px !important;*/

   position: absolute !important;

   left: 181px !important;
   top: 49px !important;

   opacity: 0.9 !important;

}
#logo:hover { opacity: 1 !important; }


/****Menu icons*****/
a[onmousedown="yt.analytics.urchinTracker('/Events/Header_4/MainNav/Home');"],
a[onmousedown="yt.analytics.urchinTracker('/Events/Header_4/MainNav/Videos');"],
a[onmousedown="yt.analytics.urchinTracker('/Events/Header_4/MainNav/Channels');"],
a[onmousedown="yt.analytics.urchinTracker('/Events/Header_4/UserNav/Subscriptions');"],
a[onmousedown="yt.analytics.urchinTracker('/Events/Header_4/UserNav/History');"],
a[onclick="yt.analytics.urchinTracker('/Events/Header_4/UserNav/Upload');"]
{

   position: absolute !important;
   color: transparent !important;

   border: 0 !important;

   width: 58px !important;
   height: 50px !important;

}
a[onmousedown="yt.analytics.urchinTracker('/Events/Header_4/MainNav/Home');"]      { background: url('http://i256.photobucket.com/albums/hh168/Q9R42/IconHome.png') no-repeat !important; left: 641px !important; top: 137px !important;   }
a[onmousedown="yt.analytics.urchinTracker('/Events/Header_4/MainNav/Home');"]:hover{ background: url('http://i256.photobucket.com/albums/hh168/Q9R42/IconHomeLit.png') no-repeat !important; }

a[onmousedown="yt.analytics.urchinTracker('/Events/Header_4/MainNav/Videos');"]      { background: url('http://i256.photobucket.com/albums/hh168/Q9R42/IconVideo.png') no-repeat !important; left: 683px !important; top: 137px !important;   }
a[onmousedown="yt.analytics.urchinTracker('/Events/Header_4/MainNav/Videos');"]:hover{ background: url('http://i256.photobucket.com/albums/hh168/Q9R42/IconVideoLit.png') no-repeat !important; }

a[onmousedown="yt.analytics.urchinTracker('/Events/Header_4/MainNav/Channels');"]      { background: url('http://i256.photobucket.com/albums/hh168/Q9R42/IconChannels.png') no-repeat !important; left: 725px !important; top: 137px !important;   }
a[onmousedown="yt.analytics.urchinTracker('/Events/Header_4/MainNav/Channels');"]:hover{ background: url('http://i256.photobucket.com/albums/hh168/Q9R42/IconChnnelsLit.png') no-repeat !important; }

a[onmousedown="yt.analytics.urchinTracker('/Events/Header_4/UserNav/Subscriptions');"]      { background: url('http://i256.photobucket.com/albums/hh168/Q9R42/IconSubscriptions.png') no-repeat !important; left: 754px !important; top: 137px !important;   }
a[onmousedown="yt.analytics.urchinTracker('/Events/Header_4/UserNav/Subscriptions');"]:hover{ background: url('http://i256.photobucket.com/albums/hh168/Q9R42/IconSubscriptionsLit.png') no-repeat !important; }

a[onmousedown="yt.analytics.urchinTracker('/Events/Header_4/UserNav/History');"]      { background: url('http://i256.photobucket.com/albums/hh168/Q9R42/IconHistory.png') no-repeat !important; left: 794px !important; top: 137px !important;   }
a[onmousedown="yt.analytics.urchinTracker('/Events/Header_4/UserNav/History');"]:hover{ background: url('http://i256.photobucket.com/albums/hh168/Q9R42/IconHistoryLit.png') no-repeat !important; }

a[onclick="yt.analytics.urchinTracker('/Events/Header_4/UserNav/Upload');"]      { background: url('http://i256.photobucket.com/albums/hh168/Q9R42/IconUpload.png') no-repeat !important; left: 838px !important; top: 137px !important; width: 30px !important;  }
a[onclick="yt.analytics.urchinTracker('/Events/Header_4/UserNav/Upload');"]:hover{ background: url('http://i256.photobucket.com/albums/hh168/Q9R42/IconUploadLit.png') no-repeat !important; }



/*User name and menu****/
#masthead-utility
{

   position: absolute !important;

   left: 0px !important;

}
a[onclick="yt.analytics.urchinTracker('/Events/Header_4/UtilLinks/Username');"]
{

   position: absolute !important;

   left: 890px !important;
   top: 80px !important;

   color: #ffffff !important;
   text-decoration: none !important;
   font-family: arial !important;

   font-size: 11px !important;

   height: 0px !important;

}
a[onclick="yt.analytics.urchinTracker('/Events/Header_4/UtilLinks/Username');"]:hover { text-shadow: 0 0 0.5em #ffffff !important; }
a[class="yt-menulink-btn yt-button yt-button-primary"]:before { content: "User: " !important; cursor: pointer !important; color: #aaaaaa !important; text-shadow: 0 0 0 #000000 !important; }


.yt-menulink-menu
{

   position: absolute !important;

   left: 887px !important;
   top: 105px !important;

   border: rgba(255,255,255, 0.1) solid 1px !important;
   width: 212px !important;

   background: rgba(0,0,0, 0.5) !important;

   z-index: 20 !important;

}
.yt-menulink-menu li a
{

   background: rgba(0, 8, 18, 0.3) !important;
   color: #ffffff !important;
   text-decoration: none !important;

   font-family: calibri !important;
   font-size: 16px !important;

}
.yt-menulink-menu li a:hover { text-shadow: 0 0 0.4em #ffffff !important; background: rgba(150,150,150, 0.1) !important; }




/*The search bar******/

#masthead-search
{

   position: absolute !important;

   left: 900px !important;
   top: 140px !important;
   background: transparent !important;
   border: 0 !important;

}
input[id="masthead-search-term"]
{

   -moz-appearance: none !important;

   background: transparent !important;
   width: 200px !important;
   padding-bottom: 5px !important;

   border: 0 !important;

   color: #ffffff !important;
   font-family: calibri !important;

}
input[id="masthead-search-term"]:hover { background: rgba(100,100,100, 0.1) !important; }
input[id="masthead-search-term"]:focus { padding-left: 2px !important; padding-top: 5px !important; background: rgba(150,150,150, 0.1) !important; }

a[onclick="if (_gel('masthead-search-term').value != '') { document['searchForm'].submit(); }; return false;"]
{

   display: inline !important;
   position: absolute !important;

   background: transparent !important;
   border: 0 !important;

   color: #ffffff !important;
   font-weight: normal !important;
   font-size: 10px !important;
   font-family: arial !important;

   left: 165px !important;
   top: 24px !important;

   opacity: 0.2 !important;

}
a[onclick="if (_gel('masthead-search-term').value != '') { document['searchForm'].submit(); }; return false;"]:hover { text-decoration: none !important; border: 0 !important; opacity: 1 !important; }

/*Intellisense****/
#completeTable, #completeTable > tbody,
#completeTable > tbody > tr,
#completeTable > tbody > tr > td,
#completeIFrame
{

   -moz-appearance: none !important;

   background: rgba(0,0,0, 0.2) !important;
   border: 0 !important;

   color: #ffffff !important;
   font-size: 16px !important;
   font-family: calibri !important;
   padding-bottom: 5px !important;
   padding-top: 5px !important;

   margin-left: -50px !important;

}
#completeTable > tbody > tr > td > span { color: #ffffff !important; }
#completeTable > tbody > tr:hover { text-shadow: 0 0 0.4em #ffffff !important; background: rgba(255,255,255, 0.1) !important; }
#completeTable
{

   border-width: 1px !important;

}
#completeIFrame
{

   
   opacity: 0.2 !important;

}
.google-ac-m
{

   width: 200px !important;

}




/*The main window*/
a[href="/account#customize/homepage"]
{

   color: transparent !important;
   width: 10px !important;
   position: absolute !important;

   font-size: 0px !important;

}
.iyt-add-remove-module-icon img
{


   position: absolute !important;
   left: 0px !important;
   top: 0px !important;

   background: url('http://i256.photobucket.com/albums/hh168/Q9R42/IconPlus.png') no-repeat !important;
   width: 30px !important;
   height: 30px !important;

   opacity: 0.7 !important;

}
.iyt-add-remove-module-icon img:hover { opacity: 1 !important; }


/*The inbox and other information*/
#stat-module-wrapper-statmodules_inbox, #statmodules_inbox,
#statmodules_inbox-content, div[class="module-item-wrapper-inner yt-rounded"],
div[class="statModule-title-wrapper"], div[class="statModule-title yt-rounded"],
.statModule-item-line
{

   border: 0 !important;
   background: transparent !important;

   color: #ffffff !important;

}
.statModule-item-line a,
#statmodules_aboutyou-content div[class="module-item-wrapper-inner yt-rounded"] div[class="statModule-item-text textBold"]
{

   color: #ffffff !important;
   text-decoration: none !important;
   border: 0 !important;

   margin-top: 10px !important;

   font-size: 11px !important;

   margin-right: 100px !important;

}
#stat-module-wrapper-statmodules_inbox
{

   position: absolute !important;
   left: 800px !important;
   top: 0px !important;

   z-index: 3 !important;

}
#statmodules_aboutyou-content
{

   position: absolute !important;
   left: 950px !important;
   top: -10px !important;

   z-index: 4 !important;

}
#stat-module-wrapper-statmodules_inbox img, #statmodules_aboutyou-content img,
#statmodules_aboutyou-titleText, #statmodules_aboutyou-title
{

   display: none !important;

}
.statModule-item-line
{

   margin-bottom: -20px !important;

}
.statModule-item-line:hover { text-shadow: 0 0 0.4em #ffffff !important; }
#statmodules_aboutyou-content .statModule-item-line
{

   margin-bottom: -25px !important;

}

a[href="/inbox?action_compose=1"] { display: none !important; }



#homepage-side-content
{

   position: absolute !important;

   left: -200px !important;
   top: -5px !important;

   opacity: 0.2 !important;

  /* width: -100px !important;*/

}
#homepage-side-content:hover
{

   opacity: 1 !important;

}





/*The video windows of the youtube start page*/
#dragdrop
{

   overflow-y: scroll !important;
   height: 600px !important;

   margin-top: 60px !important;
   width: 950px !important;

   border-width: 1px !important;
   border-top-style: solid !important;
   border-bottom-style: solid !important;
   border-color: rgba(255,255,255, 0.3) !important;

}
#dragdrop a { color: #ffffff !important; text-decoration: none !important; }
#dragdrop a:hover { text-shadow: 0 0 0.4em #ffffff !important; }

.feedmodule-body, .grid-view, .feedmodule-anchor, .feedmodule-modheader, .yt-rounded
{

   background: transparent !important;
   border: 0 !important;

   color: #ffffff !important;

}

.yt-rounded
{

   background: rgba(0,0,0, 0.2) !important;

}

div[class="video-entry yt-uix-hovercard"] a
{

   border: 0 !important;
   background: transparent !important;

   font-weight: normal !important;
 
}
.video-cell
{
 
   width: 150px !important;
   background: url('http://i256.photobucket.com/albums/hh168/Q9R42/ImgShadow.png') no-repeat !important;

   opacity: 0.9 !important;

}
.video-cell:hover { opacity: 1 !important; }

.video-cell a img
{

   width: 100px !important;
   height: 70px !important;

   margin-left: 19px !important;
   margin-top: 13px !important;

   background: transparent !important;

}
.video-cell a .video-time span
{

   background: rgba(0,0,0, 0.9) !important;
   margin-top: -47px !important;

   width: 92px !important;
   height: 12px !important;

   padding-bottom: 2px !important;

   font-family: argial !important;
   font-weight: normal !important;
   font-size: 10px !important;

   -moz-border-radius: 0% !important;

}
button[onmousedown="yt.analytics.urchinTracker('/Events/index/QuickList+AddTo')"]
{

   margin-left: 20px !important;
   opacity: 0.3 !important;

}
.yt-uix-hovercard-target
{

   font-size: 10px !important;
   /*margin-left: 10px !important;*/

}
.video-main-content
{

   margin-left: 17px !important;

}




#footer
{

   border: 0 !important;

}


}
