css:
/*
 * Vodafone Italy - Dark Gray Theme
 *
 * Autore      : Ossomasticato
 * Updated     : 07 Dicembre 2009
 * Descrizione : Modifica i colori, i logo, e qualche altra cosetta su Vodafone Italia (www.vodafone.it)
 * Utilizzo    : Usare con l'estensione Stylish per Firefox (http://userstyles.org/) o copiarla nel file userContent.css di Firefox
 */



@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("vodafone.it") {

/* Questo colora la Tag Cloud di bianco e al passaggio del mouse di rosso -------*/

.tagCloud_t a { color: #fff !important; }
.tagCloud_t a:hover a { color:#FF0000 !important; }


/* Questo fa diventare il corpo della home stile dark grey -------*/

body { background-color: #010101 !important; 
            color: #fff !important; }
            a:hover { color:#FF0000 !important; }


/* Questo cambia il colore delle lettere -------*/

.metaNav > ul > li > a {color: #fff !important; }
.metaNav > ul > li > a:hover {color: #FF0000 !important; }
.firstLevelNav a {color: #fff !important; }
.firstLevelNav a:hover {color: #FF0000 !important; }


/* Questo cambia il colore delle lettere e lo sfondo del Login -------*/

.mod1Col .modBorder {
                      background: #010101 !important; } 
.modHeightLow { background-color: #010101 !important; }
.modHeightLow a { color: #fff !important; }
.modHeightLow a:hover {color: #FF0000 !important; }
.username, #pwd, #txt  {color: #FF0000 !important; }

            
/* Questo elimina il logo vodafone -------*/

#vodafoneLogo, .globalNavShadow, .modFooter, .ricarica_custom_box, .textBtnGrey, .searchField { display: none !important }


/* Questo elimina Myfuture e Vodafone Lab -------*/

.topBarBanner { display: none !important }


/* Questo cambia il colore dello sfondo di tariffe e promo per privati -------*/

.modHeight255, .modHeight251, .modHeight168NoTitle  { background: #010101 !important }
.obj.grafico {display: none !important }
.link_list1Col a { color: #fff !important; }
.link_list1Col a:hover {color: #FF0000 !important; }


/* Questo cambia il colore delle lettere e lo sfondo di Il mio 190 fai da te -------*/

.modHeight173 { background-color: #010101 !important; }
.modHeight173 a { color: #fff !important; }
.modHeight173 a:hover {color: #FF0000 !important; } 


/* Questo cambia il colore delle lettere del footer -------*/

.footer a { color: #fff !important; }
.footer a:hover {color: #FF0000 !important; } 


.secondLevelNav a, .link_listNoCol a, .btn a  { color: #fff !important; }
.secondLevelNav a:hover, .link_listNoCol a:hover, .btn a:hover {color: #FF0000 !important; }

.txtBlock .modBorder { background: #010101 !important; }
.formAppValue a { color: #fff !important; }
.formAppValue a:hover  {color: #FF0000 !important; }
.formAppList, .modBody { 
               background: #010101 !important; }



.mod3ColEdit .modBorder {
                      background: #010101 !important; }
.mod3Col .modBorder {
                      background: #010101 !important; }
.mod1Col .modTop {
                      background: #010101 !important; }
.txtBlock .modTop {
                      background: #010101 !important; }






}
