css:
@-moz-document domain("www.nitrotype.com")
{
  body
  {
    background-color: #f779c8;
    overflow-y: scroll;
    background: none;
    overflow-x: hidden;
    position: relative;
  }

  .card--grit
  {
    background-image: url("https://i.imgur.com/Dp5iAJg.png");
    background: fixed;
  }

  html
  {
    background: fixed url("https://i.imgur.com/Dp5iAJg.png") no-repeat, #2d2e2e;
    box-sizing: border-box;
    color: #2d2e2e;
    font-family: "Montserrat", sans-serif;
    font-size: 16px;
    font-weight: 300;
    line-height: 1.6;
    background-size: 100%;
    background-position: center;
  }

  .bg--gradient
  {
    background: fixed;
  }

  .card--a
  {
    background-image: url(https://i.imgur.com/7q6hC2g.png);
    color: #ffffff;
    background-size: 150%;
    background-position: center;

  }

  .btn--primary
  {
    background: #0c0c0c;
    color: #ffffff;
  }

  .theme--pDefault .profile-title
  {
    color: #FFFFFF;
    text-shadow: 0 2px 2px rgba(2, 2, 2, 0.25);
  }

  .theme--pDefault .profile-levelNum
  {
    color: #FFFFFF;
    text-shadow: 0 2px 2px rgba(2, 2, 2, 0.25);
  }

  .tc-t
  {
    color: #ffffff;
  }

  .theme--pDefault .profile-body
  {
    color: #FFFFFF;
  }
}
@-moz-document domain(nitrotype.com)
{




  

  .tsxxs
  {
    font-size: 10px;
  }

  .bg--gradient
  {
    background: fixed;
  }

  .theme--pDefault .profile-bg
  {
    background: url("https://i.imgur.com/7q6hC2g.png") top left repeat, linear-gradient(0deg, #2d2e2e 60%, #ffffff 100%);
    color: #d3d3d3;
  }

  .btn--inky, .btn--inky:focus:not(:hover)
  {
    color: #ffffff;
  }

  .card--shadow
  {
    box-shadow: none;
  }

  .news--solo .news-body p
  {
    color: #ffffff;
    line-height: 1.8;
  }

  .news-teaser
  {
    color: #ffffff;
    margin-bottom: 40px;
  }

  .theme--pDefault .profile-bgHead
  {
    background: url("https://i.imgur.com/7q6hC2g.png") top left repeat, linear-gradient(0deg, #2d2e2e 10%, #ffffff 100%);
  }

  .theme--pDefault.is-gold .profile-bg
  {
    background: url("https://i.imgur.com/7q6hC2g.png") top left repeat, linear-gradient(0deg, #2d2e2e 10%, #ffffff 100%);
  }

  .theme--pDefault.is-guest .profile-bgHead
  {
    background: #fff0;
  }
}
