css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("docs.google.com"), 
domain("spreadsheets.google.com") {

#masthead, #gbar, #guser,  #navigation td table, .gbh, #trix-docbar {
display:none !important;
width:0 !important;
height:0 !important;
}
#doclist #goog-splitpane.goog-splitpane {
  background: #FFF !important;
}

body #doclist {
  margin-right:-3px !important;
  width: auto !important;
}


body .shelly-chrome #trix-menubar {
  border-top: none !important;
  /* background: #BACDE5 !important; */
  background: url('http://shutterb.org/images/window-bg.png') 0 0 repeat-x !important;
}

/*
.explorer-toolbar-pane, .explorer-toolbar-pane table, .explorer-toolbar-pane tr, .explorer-toolbar-pane td {
  margin: 0 !important;
  padding: 0 !important;
  background: url('http://shutterb.org/images/window-bg.png') 0 -23px repeat-x !important;
  overflow: hidden !important;
}
body .explorer-toolbar-pane {
  border-bottom: 1px solid #777 !important;
}
body .explorer-toolbar-pane .explorer-toolbar {
  border-bottom: none !important;
}
*/
.goog-toolbar, .explorer-toolbar-pane {
  padding: 0 3px !important;
  background: url('http://shutterb.org/images/window-bg.png') 0 -23px repeat-x !important;
  overflow: visible !important;
  border-bottom: 1px solid #777 !important;
  border-top: 1px solid #CCC !important;
}
.goog-toolbar b, .explorer-toolbar-pane b {
  font-weight: normal !important;
}
.pathview {
  padding: 1px 0 !important;
  background: none !important;
}
.statusbarview {
  position: absolute !important;
  display: inline !important;
  right: 80px !important;
  top: 1px !important;
  height: auto !important;
  background: none !important;
}
.path-inner {
  margin: 0 !important;
  padding: 2px 0 !important;
  height: auto !important;
  text-align: center !important;
}
.path-name, .pager-status {
  border: none !important;
  padding: 1px 2px 0 !important;
  background: none !important;
  font-size: 13px !important;
  font-weight: normal !important;
  color: #777 !important;
  letter-spacing: 0.02em !important;
  text-shadow: 0 1px 0 rgba(255,255,255,0.4) !important;
  -moz-text-shadow: 0 1px 0 rgba(255,255,255,0.4) !important;
  -webkit-text-shadow: 0 1px 0 rgba(255,255,255,0.4) !important;
  cursor: default !important;
}

.goog-toolbar-menu-button, .goog-imageless-menu-button, .goog-imageless-popup-button, .goog-imageless-button, .goog-toolbar-button {
  padding: 2px 2px 0 1px !important;
  margin: 1px 2px 2px !important;
  background: url('http://jasonmiller.me/files/leopard-button-bg.png') center repeat-x !important;
  border: 1px solid #666 !important;
  border-radius: 6px !important;
  -moz-border-radius: 6px !important;
  -webkit-border-radius: 6px !important;
  -moz-box-shadow: 0 1px 0 rgba(255,255,255,0.4);
  /*
  background: url('http://www.outbackphoto.com/workflow/wf_74/gradient.jpg') 0 -110px repeat-x !important;
  border: 1px solid rgba(0,0,0,0.2) !important;
  border-top-color: rgba(255,255,255,0.5) !important;
  border-bottom-color: rgba(0,0,0,0.4) !important;
  */
}
.goog-toolbar-menu-button-hover, .goog-imageless-menu-button-hover, .goog-imageless-popup-button-hover, .goog-imageless-button-hover {
}
body .goog-toolbar-button-active, body .goog-imageless-button-active, body .goog-toolbar-menu-button-open, body .goog-imageless-menu-button-open, body .goog-imageless-popup-button-open, body .goog-imageless-button-active {
  background-image: url('http://jasonmiller.me/files/leopard-button-bg-active.png') !important;
}

.goog-toolbar-menu-button, .goog-imageless-menu-button, .goog-imageless-popup-button, .goog-imageless-menu-button-content, .goog-imageless-button, .goog-imageless-button-content, 
.goog-toolbar-button, .goog-imageless-menu-button-content, .goog-toolbar-menu-button-caption, .goog-toolbar-button-caption {
  padding-top: 0 !important;
  padding-bottom: 0 !important;
  color: #333 !important;
  font-weight: normal !important;
  text-shadow: 0 1px 0 rgba(255,255,255,0.5);
  -moz-text-shadow: 0 1px 0 rgba(255,255,255,0.5);
  -webkit-text-shadow: 0 1px 0 rgba(255,255,255,0.5);
}
.goog-toolbar-menu-button-inner-box, .goog-toolbar-menu-button-outer-box {
  padding: 0 1px 0 3px !important;
  margin: 0 !important;
  border: none !important;
}
.goog-toolbar-button-inner-box, .goog-toolbar-button-outer-box, .goog-imageless-menu-button-outer-box, .goog-imageless-button-outer-box {
  padding: 0 3px !important;
  margin: 0 !important;
  border: none !important;
  background: none !important;
}
.goog-imageless-button-inner-box, .goog-imageless-button-pos, 
.goog-imageless-button-top-shadow, .goog-imageless-button-content,
.goog-imageless-menu-button-inner-box, .goog-imageless-menu-button-pos, 
.goog-imageless-menu-button-top-shadow, .goog-imageless-menu-button-content, 
.goog-imageless-popup-button-inner-box, .goog-imageless-popup-button-outer-box, .goog-imageless-popup-button-pos, 
.goog-imageless-popup-button-top-shadow, .goog-imageless-popup-button-content {
  padding-top: 0 !important;
  padding-bottom: 0 !important;
  border: none !important;
  background: none !important;
}
body .goog-imageless-button-inner-box {
  padding-top: 1px !important;
  padding-bottom: 1px !important;
}
body .goog-imageless-popup-button-dropdown,
body .goog-imageless-menu-button-dropdown {
  padding: 0 !important;
  top: 1px !important;
  left: 2px !important;
  margin: 0 -3px 0 1px !important;
}
.goog-button-content, 
.goog-menu-button-content, 
.goog-imageless-button-content, 
.goog-imageless-menu-button-content, 
.goog-imageless-popup-button-content {
  line-height: 1.3em !important;
}
.goog-imageless-button-content {
  margin-top: -1px !important;
  margin-bottom: -1px !important;
}
.explorer-toolbar-item-select-icon {
  padding: 0 !important;
  margin: -2px 2px -3px 0 !important;
}
.contentcreationpane .goog-toolbar-item-new .goog-imageless-menu-button-outer-box {
  margin-top: 0px !important;
  margin-bottom: -1px !important;
}
.contentcreationpane .goog-toolbar-item-new .goog-imageless-menu-button-dropdown {
  top: -1px !important;
}






/* document list */

.doclist-header {
  height: auto !important;
  border: none !important;
  cursor: default !important;
}
.doclist-header-table {
  border-bottom: 1px solid rgba(0,0,0,0.3) !important;
  cursor: default !important;
}
.doclist-header td {
  height: auto !important;
  padding-top: 1px !important;
  padding-bottom: 0 !important;
  /* background: url('http://www.outbackphoto.com/workflow/wf_74/gradient.jpg') 0 -80px repeat-x !important; */
  background: url('http://jasonmiller.me/files/leopard-bar-bg.png') center repeat-x !important;
  border: none !important;
  border-top: 1px solid #FFF !important;
  border-right: 1px solid #D6D6D6 !important;
  /* border-left: 1px solid #F6F6F6 !important; */
  color: #222 !important;
  text-shadow: 0 1px 0 rgb(255,255,255) !important;
  -moz-text-shadow: 0 1px 0 rgb(255,255,255) !important;
  -webkit-text-shadow: 0 1px 0 rgb(255,255,255) !important;
  cursor: default !important;
}
.doclist-header-star-icon {
  top: -1px !important;
  left: -1px !important;
}




.contentcreationpane {
  background: none !important;
  padding: 0 !important;
  margin: 0 !important;
  border: none !important;
}
.contentcreationpane .goog-toolbar {
  margin: 0 !important;
  padding: 0 3px !important;
  /*
  background: url('http://www.outbackphoto.com/workflow/wf_74/gradient.jpg') 0 -80px repeat-x !important;
  border: none !important;
  border-bottom: 1px solid #999 !important;
  */
}
.navpane-top-list {
  padding-top: 0 !important;
}

.goog-splitpane-first-container, .navpane {
  background: #D6DDE5 !important;
}
.navpane, .navpane-container-scrollable, .navpane-top-list, .nav-tree-folder-view,
.navpane .goog-menu, .nav-tree-folder-view .goog-list {
  background: none !important;
}

.nav-tree-folder-view {
  padding-top: 0 !important;
  border-top: 1px solid rgba(0,0,0,0.15) !important;
}
.nav-tree-folder-view .goog-list {
  margin-top: 0 !important;
  border-top: 1px solid rgba(255,255,255,0.4) !important;
}

body .navpane .goog-menuitem, body .navpane .detroit-menuitem, body .navpane .goog-option {
  background-image: url('http://jasonmiller.me/files/leopard-selection-alt.png') !important;
  padding-bottom: 1px !important;
}
.goog-splitpane-handle {
  width: 2px !important;
  border: none !important;
  /* background: url('http://shutterb.org/images/window-bg.png') top right no-repeat #D6DDE5 !important; */
  background: #BBB !important;
  cursor: e-resize !important;
}


.doclist-tbody {
  border-collapse: collapse !important;
  border: none !important;
}
.doclist-tr, .doclist-tr-selected {
  background: url('http://jasonmiller.me/files/leopard-selection.png') 0 -999px repeat-x !important;
  border: none !important;
}

.doclist-tr:nth-child(even) td {
  background-color: #FFF;
}
.doclist-tr:nth-child(odd) td {
  background-color: #ECF3FE;
}


body .doclist-tr-selected {
  background-color: #3875D7 !important;
  color: #FFF !important;
}
body .doclist-tr-selected a {
  color: #FFF !important;
}
body .doclist-tr-selected .e-s, body .doclist-tr-selected .doclist-lastcollaborator {
  color: #BBB !important;
}
.doclist-tr td, .doclist-tr-selected td {
  border: none !important;
}
.doclist-dateseparator {
  padding: 2px 0 1px !important;
  background: url('http://www.outbackphoto.com/workflow/wf_74/gradient.jpg') 0 -76px repeat-x !important;
  border: none !important;
  border-top: 1px solid #E6E6E6 !important;
  border-bottom: 1px solid #DDD !important;
  letter-spacing: 0.05em !important;
  font-weight: normal !important;
  font-size: 11px !important;
  color: #AAA !important;
  text-shadow: 0 1px 0 rgba(255,255,255,0.8) !important;
  -moz-text-shadow: 0 1px 0 rgba(255,255,255,0.8) !important;
  -webkit-text-shadow: 0 1px 0 rgba(255,255,255,0.8) !important;
}


.doclist-dragdrop-drag {
  opacity: 0.8;
  border-radius: 5px !important;
  -moz-border-radius: 5px !important;
  -webkit-border-radius: 5px !important;
}












.goog-menu, .detroit-menu, .folders-popup {
  border: 1px solid rgba(0,0,0,0.4) !important;
  background: #FFF !important;
  opacity: 0.95 !important;
  padding-top: 4px !important;
  padding-bottom: 4px !important;
  border-radius: 5px !important;
  -moz-border-radius: 5px !important;
  -webkit-border-radius: 5px !important;
  box-shadow: 0 7px 15px rgba(0,0,0,0.5) !important;
  -moz-box-shadow: 0 7px 15px rgba(0,0,0,0.5) !important;
  -webkit-box-shadow: 0 7px 15px rgba(0,0,0,0.5) !important;
}
body .navpane-container-scrollable .goog-menu {
  border: none !important;
  border-radius: 0 !important;
  -moz-border-radius: 0 !important;
  -webkit-border-radius: 0 !important;
}
.goog-menuitem, .detroit-menuitem, .goog-option {
  position: relative !important;
  margin-top: 0 !important;
  margin-bottom: 0 !important;
  padding-top: 2px !important;
  /*
  padding-bottom: 0 !important;
  height: 17px !important;
  */
  height: auto !important;
  padding-bottom: 2px !important;
  background: url('http://jasonmiller.me/files/leopard-selection.png') 0 -999px repeat-x !important;
  font-weight: normal !important;
  cursor: default !important;
}
body .detroit-menuitem .detroit-menuitem-container {
  top: -1px !important;
}
body .detroit-menu .goog-menuitem {
  top: 0px !important;
}
body .goog-menuitem-icon {

}
body .detroit-menuitem-icon {
  top: -1.6px !important;
  opacity: 0.7 !important;
}
body .goog-menuitem-highlight, body .detroit-menuitem-highlight, body .goog-option-selected {
  background-position: 0 0 !important;
  color: #FFF !important;
  text-shadow: 0 -1px 0 rgba(0,0,0,0.3) !important;
  -moz-text-shadow: 0 -1px 0 rgba(0,0,0,0.3) !important;
  -webkit-text-shadow: 0 -1px 0 rgba(0,0,0,0.3) !important;
}
body .goog-menuitem-highlight .goog-menuitem-content, body .goog-option-selected .goog-menuitem-content {
  color: #FFF !important;
}
body .goog-submenu-arrow, body .detroit-submenu-arrow {
  color: #777 !important;
}
body .goog-menuitem-highlight .goog-submenu-arrow, 
body .detroit-menuitem-highlight .detroit-submenu-arrow,
body .detroit-menuitem-highlight .goog-submenu-arrow {
  color: #FFF !important;
}
body .goog-list-menu-container .goog-option .goog-listitem-container {
  top: -2px !important;
  padding: 0 !important;
  margin: 0 !important;
}
body .detroit-menuitem-caption {
  margin-top: 2px !important;
}






/* loading indicator */
body .loadingstatusbox {
  top: -20px !important;
  padding: 20px 10px 3px !important;
  background: rgba(0,0,0,0.5) !important;
  border: 2px solid rgba(255,255,255,0.7) !important;
  border-radius: 5px !important;
  -moz-border-radius: 5px !important;
  -webkit-border-radius: 5px !important;
  color: #FFF !important;
  text-shadow: 0 -1px 0 #000 !important;
  -moz-text-shadow: 0 -1px 0 #000 !important;
  -webkit-text-shadow: 0 -1px 0 #000 !important;
  box-shadow: 0 0 10px #000 !important;
  -moz-box-shadow: 0 0 10px #000 !important;
  -webkit-box-shadow: 0 0 10px #000 !important;
}






/* Dialogs */

.dialog-bg {
  opacity: 1 !important;
  background: rgba(0,0,0,0.4) !important;
}

.dialog, .modal-dialog {
  padding: 0 0 15px !important;
  background: url('http://jasonmiller.me/include/application.js/style/default/images/StatusBar.png') left bottom repeat-x !important;
  border: none !important;
  border-radius: 6px 0 !important;
  -moz-border-radius-topleft: 6px !important;
  -moz-border-radius-topright: 6px !important;
  -webkit-border--top-left-radius: 6px !important;
  -webkit-border-top-right-radius: 6px !important;
  box-shadow: 0 5px 35px rgba(0,0,0,0.9) !important;
  -moz-box-shadow: 0 5px 35px rgba(0,0,0,0.9) !important;
  -webkit-box-shadow: 0 5px 35px rgba(0,0,0,0.9) !important;
  border-top: 1px solid #EEE !important;
}
.dialog-title, .modal-dialog-title {
  padding: 2px 0 !important;
  background: url('http://shutterb.org/images/window-bg.png') 0 0 repeat-x !important;
  border-bottom: 1px solid #888 !important;
  border-radius: 5px 0 !important;
  -moz-border-radius-topleft: 5px !important;
  -moz-border-radius-topright: 5px !important;
  -webkit-border--top-left-radius: 5px !important;
  -webkit-border-top-right-radius: 5px !important;
  text-align: center !important;
  cursor: default !important;
}
.dialog-title-text, .modal-dialog-title-text {
  font-size: 13px !important;
  font-weight: normal !important;
  color: #111 !important;
  letter-spacing: 0.02em !important;
  text-shadow: 0 1px 0 rgba(255,255,255,0.7) !important;
  -moz-text-shadow: 0 1px 0 rgba(255,255,255,0.7) !important;
  -webkit-text-shadow: 0 1px 0 rgba(255,255,255,0.7) !important;
  cursor: default !important;
}
.dialog-title-close, .modal-dialog-title-close {
  position: absolute !important;
  left: 5px !important;
  top: 1px !important;
  width: 16px !important;
  height: 16px !important;
  padding: 0 !important;
  margin: 0 !important;
  background: url('http://jasonmiller.me/include/application.js/style/default/images/icon_close_over.png') center no-repeat !important;
  border: none !important;
  cursor: default !important;
}
.modal-dialog-title-close .close_box {
  display: none !important;
}

.dialog-content, .modal-dialog-content {
  background: #FFF !important;
  border: none !important;
}

}
