css:
@-moz-document domain(www.solarweb.com) {
  body {
    background-color: white;
    overflow-x: hidden;
  }
  #powerWidget {
      width: 100%;
  }
  .second-level-nav, .layout-footer, .navbar, .margin-system > .col-lg-4, .margin-system > .col-sm-6 {
      display: none;
  }
  .layout-content, .container-fluid {
    padding: 0;
  }
  .u-max-page-width {
 	max-width: 100vh;
  }
}
