@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("php.net") {


  /* Global Definitions */

  body, input, textarea, select, option {
    font-family: arial, helvetica, sans-serif !important;
    }

  code, pre, tt {
    font-family: Consolas, "Andale Mono WT", "Andale Mono", "Lucida Console", Monaco, "Courier New", Courier, monospace !important;
  }

  blockquote {
    background: transparent !important;
    border: 0 !important;
    border-left: 2px solid #999 !important;
    margin: 0 !important;
    padding: 0 1.2em !important;
    }

  pre {
    background-color: transparent !important;
    border: 0 !important;
    margin: 0 !important;
    padding: 0 !important;
    }

  p { margin: 0.8em 0 !important; }

  h3 {
    font-size: 110% !important;
    margin-top: 1.5em !important;
    }

  tt.parameter { font-style: normal !important }

  #headmenu { bottom: 4px !important }
  #headsearch form, #headsearch p { margin: 5px 0 !important }

  #content { padding: 14px !important; }

  div#usernotes div.note { padding: 6px !important; }
  div#usernotes div.text {
    margin-top: 6px !important;
    padding: 3px !important;
    }



  /* Definition Lists */

  dl dd {
    margin: 0 0 0 3em !important;
    padding: 0 !important;
    }

  div.section dt {
    background: #fff !important;
    border: 0 !important;
    border-top: 1px dashed #ccc !important;
    padding-top: 0.8em !important;
    }

  dd.listitem span.simpara {
    display: block !important;
    margin: 0.8em 0 !important;
    }

  div.refsect1 dd {
    border: 0 !important;
    }

  div.section dd {
    }

  div.section dd,
  div.refsect1 dd {
    background-color: transparent !important;
    margin: -4px 0 0 2em !important;
    /* famfamfam book icon, uncomment the following two lines */
    /* background: transparent url(data:image/gif;base64,) no-repeat scroll 0px 2px !important; */
    /* padding: 0 0 0 24px !important; */
    }



  /* Generic Sections */

  .refentry table td { text-indent: 0 !important }

  div.refsect1 {
    background-color: #fff !important;
    border: 0 !important;
    margin: 1.6em 0 0 0 !important;
    padding: 0 !important;
    }

  div.refsect1 dl {
    border: 0 !important;
    margin: 1em 2em !important;
    padding: 0 !important;
    }

  div.refsect1 h3 {
    border: none !important;
    margin: 0 0 0.4em !important;
    padding-top: 0 !important;
    }

  div.refsect1 span.term {
    border-bottom: 1px dashed #999 !important;
    }



  /* Synopsis */

  div.refsect1 div.methodsynopsis,
  div.refsect1 div.constructorsynopsis,
  div.refsect1 div.destructorsynopsis {
    background-color: #f6f6ff !important;
    border: 1px dotted #aac !important;
    margin: 8px 0 !important;
    padding: 0.5em 0.7em !important;
    }



  /* Examples */

  div.classsynopsis div.ooclass, div.example p {
    border: 0 !important;
    margin: 0 !important;
    padding: 0.3em 0 !important;
    text-indent: 0 !important;
    }

  div.example, div.informalexample, div.classsynopsis div.ooclass {
    background-color: transparent !important;
    border: 0 !important;
    }

  div.example div.programlisting {
    border: 0 !important;
    margin: 0 !important;
    padding: 0 !important;
    }

  div.programlisting .phpcode,
  table .phpcode,
  div.classsynopsis,
  div.informalexample .phpcode {
    background-color: #dde3ff !important;
    border: 1px dotted #abe !important;
    margin: 0 !important;
    }

  div.example div.para,
  div.example div.simpara,
  div.example div.para p,
  div.informalexample p {
    background-color: #fff !important;
    border: 0 !important;
    margin: 0.8em 0 !important;
    padding: 0 !important;
    }

  div.screen {
    background-color: #030 !important;
    border: 0 !important;
    color: #0c0 !important;
    font-weight: bold !important;
    margin: 5px 0 !important;
    padding: 12px 16px !important;
    }



  /* Tables */

  table.doctable td,
  table.doctable th,
  table.doctable caption,
  table.informaltable td,
  table.informaltable th,
  table.informaltable caption {
    padding: 4px 12px 4px 8px !important;
    margin: 0 !important;
    }

  table.doctable thead th,
  table.informaltable thead th {
    background-color: #679 !important;
    border-bottom: 2px solid #003 !important;
    color: #fff !important;
    font-size: 90% !important;
    text-align: left !important;
    }

  table.doctable,
  table.informaltable {
    border-spacing: 0 !important;
    }

  table.doctable tbody td,
  table.informaltable tbody td {
    background-color: #f3f3ff !important;
    border-bottom: 1px dotted #cce !important;
    }


  /* Messages */

  div.warning, div.caution, div.tip, div.information {
    margin: 10px 0 !important;
    padding: 15px 24px 8px 24px !important;
    text-align: left !important;
    }

  div.warning b.warning,
  div.caution b.caution,
  div.tip b.tip,
  div.information b.information {
    font-size: 100% !important;
    text-transform: uppercase !important;
    }

  div.warning {
    background-color: #fff6f6 !important;
    background-image: url(data:image/png;base64,) !important;
    background-position: 12px 14px !important;
    border: 2px solid #600 !important;
    color: #500 !important;
    padding-left: 50px !important;
    }

  div.caution {
    background-image: url(data:image/png;base64,) !important;
    background-repeat: no-repeat !important;
    background-position: 12px 14px !important;
    border: 2px solid #440 !important;
    padding-left: 50px !important;
    }

  div.information, div.tip {
    background-image: url(data:image/png;base64,) !important;
    background-position: 12px 14px !important;
    padding-left: 50px !important;
    }

  div.warning h1 {
    color: #500 !important;
    font-size: 120% !important;
    margin: 0.8em 0 !important;
    }

}
