css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com") {

/*globals*/

a{color:#d215de!important;}
.UIContentBox, .commentable_item .ufi_section {background-color:#fabfff!important;}
.inputbutton {background-color:#e326ef!important; border-color: #fabfff #af6cb3 #af6cb3 #fabfff!important;}
#save, .ff3 span.UIFormButton .UIButton_Text , .inputsubmit {background-color:#e326ef!important; border-color: #fabfff #af6cb3 #af6cb3 #fabfff!important;}
.inputbutton:hover{background-color:#f548ff!important;}
#save:hover, .ff3 span.UIFormButton .UIButton_Text:hover, .inputsubmit:hover{background-color:#f548ff!important;}
.inputaux {background-color:#fabfff!important;}
.ci_submit_button provider_logo{display:none!important;}

.UIButton_Text *{background-color:#FABFFF !important;padding-right:1px!important;}

h2.dialog_title{background-color:#e326ef!important;border-color:#d215de!important;}
.tokenizer, .UIComposer_InputArea{border-color:#d215de!important;}

.PillFilter_pillfilter .PillFilter_filter.Tabset_selected div {background:#d89ddd none!important;border-radius:10px!important;-webkit-border-radius:10px!important;-moz-border-radius:10px!important;}

/*header bar*/
.fb_menu .fb_menu_link{color:#fff!important;}
#menubar_container{background-color:#e326ef!important;}
.fb_menu_link:hover{background-color:#d215de!important;}
#universal_search_input, #universal_search{border-color:#d215de!important;}
.inputtext, .inputpassword{border-color:#d215de!important;}
.search_mag_glass, #universal_search #universal_search_submit label{background-color:#e864f0!important;border-color:#d215de!important;}

/*sidebar*/
.UIFilterList .selected{background-color:#e864f0!important;border-color:#d215de!important;}
.UIFilterList_Item .UIFilterList_ItemLink:hover {background-color:#fcdfff!important;}
.UIFilterList .selected:hover{background-color:#e864f0!important;}
.UIFilterList_ItemLink, .UIFilterList_Footer, .UIFilterList_EditLink{border-top-color:#af6cb3!important;}

/*footer*/

#presence_ui{background: #fabfff none!important;}

#buddy_list_tab, #presence .presence_bar_button .inner_button, #presence_notifications_tab, #presence_applications_tab{border-color:#e9aeee!important;}

.buddy_list a.friend span:hover, .buddy_list a.friend:hover {color:#fff!important;background-color:#d215de!important;}


#presence_bar .presence_menu_opts h2, presence_applications_tab{background-color:#e326ef!important; border-color:#af6cb3!important;}
.application_menu_header{background-color:#fcdfff!important;}

/*Profile specific stuff*/
.profile_top_wash{background:#fcdfff none!important;border-bottom-color:#fabfff!important;border-bottom-width:1px!important;}
.profile .box .box_header, .profile .basic_info_summary .box_header UITitle UITitle_h5, .UIProfileBox_Header{background-color:#fcdfff!important;border-top-color:#fabfff!important;}

.box{border-top-color:#fabfff!important;border-left-color:#fabfff!important;}

 .profile_actions a:hover{color:#fff!important;background-color:#d215de!important;}
* box_editor:hover{background-color:#d215de!important;}

.profile .top_bar ul.tabs li a.tab_link {background-color:#fcdfff!important;border-color:#fabfff #fabfff #fabfff #fabfff!important;}
.profile .top_bar ul.tabs li a.tab_link:hover{background-color:#e9aeee!important;}
.profile .top_bar ul.tabs li.selected a.tab_link {background-color:#fff!important;}
.UIComposer_SubmitButton UIButton UIButton_Blue UIFormButton{background-color:#e326ef!important; border-color: #fabfff #af6cb3 #af6cb3 #fabfff!important;}
.UIComposer_SubmitButton UIButton UIButton_Blue UIFormButton:hover{background-color:#f548ff!important;}

/*photos pages*/
.UIPhotoGrid_Container{background-color:#fcdfff!important;border-color:#fabfff!important;}
.UIPhotoGrid_Image{border-color:#fabfff!important;}
.UIPhotoGrid_Image:hover{border-color:#d89ddd!important;}

.extra_content{background-color:#fcdfff!important;border-color:#fabfff!important;}
.main_content{background-color:#fdefff!important;border-color:#fabfff!important;}

/*notes page*/
.share_and_hide .share_a{border-color:#d215de!important;}

/*PM page*/
.UINestedFilterList_Item_Link:hover{background-color:#fdefff!important}

.UINestedFilterList_Selected .UINestedFilterList_Item_Link, .UINestedFilterList_Selected .UINestedFilterList_Item_Link:hover, .UINestedFilterList_SubItem_Selected .UINestedFilterList_SubItem_Link {background-color:#e326ef!important;border-color:#d215de!important;}

/*this one uses an CSS hack to recreate an image, which is pretty awesome, and something the actual site should do.*/

.GBTabset .selected, .GBTabset .selected span, .GBTabset .selected span span, .GBTabset .selected span span span {background:#d89ddd none!important;border-radius:10px!important;-webkit-border-radius:10px!important;-moz-border-radius:10px!important;}

.GBThreadRow .line {color:#d215de!important;}
.GBThreadRow .unread {background-color:#fdefff!important;}



/*settings pages*/
.toggle_tabs li a.selected {background-color:#d215de!important;color:#fff!important;}

/*careers page*/
.careers_header {background:#fcdfff none!important;border-bottom-color:#fabfff!important;border-bottom-width:1px!important;}

.careers #masthead .UITabStructure_Tabs li a {background-color:#fcdfff!important;border-color:#fabfff #fabfff #fabfff #fabfff!important;}
.careers #masthead .UITabStructure_Tabs li a:hover {background-color:#e9aeee!important;}

.careers #masthead .UITabStructure_Tabs li a.UITabStructure_TabSelected {background-color:#fff!important;}

/*misc stuff*/
.action_links_title .comment_link, .action_links_bottom .comment_link, .feedback_show_link, .feedback_hide_link, .UIActionLinks .comment_link{color:#d215de!important;}

button.as_link{color:#d215de!important;}

.UIMeter_Progress {background-color:#f548ff!important;border-color:#d215de!important;}

#FriendsPage_PageletContainerContent{background-color:#fcdfff!important;}

.selected .UIFilterList_ItemLink {background-color:#e864f0!important;}

.presence_popout {background-color:#fabfff!important;}

#chat_tab_bar .chat_header {background-color:#fabfff!important;}

/*Image replacement*/
#edit_profilepicture_icon {background:url(http://trezoid.webs.com/FBPink/penimageFBPink.gif)no-repeat scroll right bottom!important;}

#edit_profilepicture_icon:hover{background-position:right top!important;}

.box_editor, .edit_link, .profile .top_bar ul.tabs li.selected a.edit   {background:transparent url(http://trezoid.webs.com/FBPink/penimageFBPink.gif) no-repeat scroll left bottom!important;}
.box_editor:hover, .edit_link:hover, .profile .top_bar ul.tabs li.selected a.edit:hover{background-position:left top!important;}

#fb_menubar_logo .fb_logo_img {background:transparent url(http://trezoid.webs.com/FBPink/SpriterepFBPink.png) no-repeat scroll -480px -83px!important;}

#universal_search #universal_search_submit label span {background:transparent url(http://trezoid.webs.com/FBPink/SpriterepFBPink.png) no-repeat scroll -429px -65px!important;}
#fb_menubar_logo .fb_logo_link:hover .fb_logo_img {background-position:-480px -66px!important;}

.commentable_item .comment_box_nub {background:transparent url(http://trezoid.webs.com/FBPink/SpriterepFBPink.png) no-repeat scroll -930px -69px!important;}
}
