css:
@-moz-document url-prefix("https://www.brick-hill.com/") {
nav .push-left {
    padding-left: 120px;
}
nav .push-left:before  {
    content: url(https://cdn.discordapp.com/attachments/437245845591687169/945280618361671740/ilovelean.png);
    position: absolute;
    left: -85px;
    width: 1px;
    margin-bottom: 0;
    pointer-events: none;
}
.primary .grid {
    overflow: hidden;
    position:relative;
   z-index:99;
}
nav .info {
    border: 1px solid #BD00DF;
}
.card .top:not(.absolute).blue {
    color: #BD00DF;
    background-color: #2e2f32;
    border-color: #BD00DF;
}
.card .top:not(.absolute).red {
    color: #BD00DF;
    background-color: #2e2f32;
    border-color: #BD00DF;
}
.card .top:not(.absolute).orange {
    color: #BD00DF;
    background-color: #2e2f32;
    border-color: #BD00DF;
}
.card .top:not(.absolute).green {
    color: #BD00DF;
    background-color: #2e2f32;
    border-color: #BD00DF;
}
.card .top:not(.absolute) {
    color: #BD00DF;
    background-color: #2e2f32;
    border-color: #BD00DF;
}
.status-dot.online {
    background-color: #BD00DF;
}
.pages .page.active {
    color: #BD00DF;
}
button:not(.plain):not([disabled]).blue, .button:not(.plain):not([disabled]).blue {
    color: #BD00DF;
    background-color: #2e2f32;
    border-color: #BD00DF;
}
button:not(.plain):not([disabled]).orange, .button:not(.plain):not([disabled]).orange {
    color: #BD00DF;
    background-color: #2e2f32;
    border-color: #BD00DF;
}
button:not(.plain):not([disabled]).red, .button:not(.plain):not([disabled]).red {
    color: #BD00DF;
    background-color: #2e2f32;
    border-color: #BD00DF;
}
button:not(.plain):not([disabled]).green, .button:not(.plain):not([disabled]).green {
    color: #BD00DF;
    background-color: #2e2f32;
    border-color: #BD00DF;
}
.crate-types .active, .crate-types :hover {
    border-left: 4px solid #BD00DF;
}
.card .footer .playing {
    color: #BD00DF;
}
.favorite-text {
    color: #BD00DF;
}
.download-page .holder.legacy button.nh-button {
    border-color: #BD00DF;
}
.red-text {
    color: #BD00DF;
}
.blue-text {
    color: #BD00DF;
}
.green-text {
    color: #BD00DF;
}
.alert.warning {
    background-color: #BD00DF;
}
.input-group .input-button {
    border-left: 1px solid #b5416b;
    background-color: #BD00DF;
}

.forum-quote:hover {
    color: #BD00DF;
}

.forum-reply:hover {
    color: #BD00DF;
}

.report:hover {
    color: #BD00DF;
}
}

@-moz-document url-prefix("https://www.brick-hill.com/dashboard/") {
input[type="text"] {
    border-right: none;
}
}
