css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bibliotik.org") {
body, select, input, textarea {

    font-family: Verdana!important;
    font-size: 10px !important;
background-color: #EEE !important;

}

.pagination a:visited { color: #ddd6fa !important; !important; }
h1 {

    font-size: 14px !important;

}

h2 {
    font-size: 10px !important;
}
td strong a {
font-size: 12px !important;
color: #ff6655 !important;
display: block !important;
}
td {
padding: 5px !important;
}
#description {
font-size: 14px !important;
}
#pre_header, #header_nav_links, .details_torrents, .details_requests, .details_collections, .details_authors, .details_artists, .details_publishers, .details_peers, .details_snatches, #published, #details_tags, #creatorlist, #footer_nav, .pagination, .conversation_headline  {
    font-size: 10px !important;
}

#header_notifications ul {
    font-size: 12px !important;
}

#title {
    font-size: 14px !important;
}

}
