css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://www.implight.net/"), url-prefix("http://en.implight.net/") {
body{
color:#fff !important;
background-color:#000 !important;
}
input, textarea, select{
color:#777 !important;
background-color:#111 !important;
}
#content{
background-color:#000 !important;
}
#contentstart{
background-color:#000 !important;
}
tbody{
border-top:1px solid #000 !important;
}
#mission{
background-color:#000 !important;
color:#969696 !important;
border-top:2px solid #232323 !important;
border-bottom:2px solid #232323 !important;
}
.messages{
background-color:#111 !important;
border:1px solid #333 !important;
}
td#home{
background:#000 !important;
}
td#home a:link img,td#home a:visited img{
background:#000 url("data:image/gif;base64,") !important;
}
td#home a:hover img{
background:#000 url("data:image/gif;base64,") !important;
}
#site-info{
background:#000 !important;
}
#sidebar-left,#sidebar-right{
background:#00050f !important;
}
#sidebar-left{
border-right:3px solid #0a0a0a !important;
}
#sidebar-right{
border-left:3px solid #0a0a0a !important;
}
div.admin-panel h3{
background-color:#c96 !important;
color:#000 !important;
}
div.admin-panel .body{
background:#00050f !important;
}
#aggregator .feed-source{
background-color:#111 !important;
border:1px solid #333 !important;
}
#tracker th{
background-color:#0a0a0a !important;
border-bottom:1px solid #222 !important;
border-right:1px solid #222 !important;
border-left:1px solid #050505 !important;
}
#tracker tr.even,#tracker tr.odd{
background-color:#000 !important;
}
#tracker td{
border-bottom:1px solid #444 !important;
}
tr.even,tr.odd{
border-bottom:1px solid #333 !important;
}
#forum{
background-color:#000 !important;
}
#forum table{
border:2px solid #333 !important;
}
#forum table tr th{
background:#111 !important;
border-bottom:1px solid #333 !important;
}
#forum tr.odd,#forum tr.odd td.last-reply{
background:#111 !important;
}
#forum tr.even,#forum tr.even td.last-reply{
background:#222 !important;
}
#forum td.container{
color:#fff !important;
background:#000 !important;
border:2px solid #333 !important;
}
#forum td.container a{
color:#eee;
}
#forum td.container a:visited{
color:#ddd;
}
#primary-menu{
background-color:#000 !important;
border-bottom:3px solid #333 !important;
}
#secondary-menu{
color:#777 !important;
background-color:#111 !important;
border-bottom:3px solid #333 !important;
}
.secondary-links,.secondary-links a:link,.secondary-links a:visited{
color:#fff !important;
}
.secondary-links a:hover{
color:#fff !important;
}
#navlist li{
border-left:1px solid #777 !important;
}
#subnavlist li,ul.primary-links li,ul.secondary-links li{
border-left:1px solid #777 !important;
}
tbody{
border-top:1px solid #333 !important;
}
tbody th{
border-bottom:1px solid #333 !important;
}
table#footer-menu{
border-top:3px solid #333 !important;
border-bottom:3px solid #333 !important;
background-color:#111 !important;
color:#777 !important;
}
#footer-menu .primary-links,#footer-menu a:link,#footer-menu a:visited{
color:#eee !important;
}
#footer-menu a:hover{
color:#fff !important;
}
#footer-menu .primary-links h1,#footer-menu .primary-links h2,#footer-menu .primary-links h3{
color:#eee !important;
}
.messages {
color:#f11 !important;
}
.preview .node, .preview .comment{
background-color:#111 !important;
}
.tabs ul.primary li.active a{
background:#333 url("data:image/png;base64,") left top no-repeat !important;
}
.tabs ul.primary li a{
background:#000 url("data:image/png;base64,") left top no-repeat !important;
}
}
