css:
@-moz-document url-prefix("https://opalium.dev/") {
header {
    background-color:#c6eaff
}                                            
body {
    background-color:#e1f8ff
}
.post-condensed, .post, .box {
    background-color:#c7d7ef
}
.post-condensed, .post, .box {
    color:#000
}                    
.post-attribution, .box-attribution {
    color:#000
}                                           
.box h3 {
    background-color:#c6eaff
}
.btn {
    background-color:#c6eaff
}
.post-hidden {
    background-color:#c6eaff
}                                         
.post-removed, .post-banned {
    background-color:#c6eaff
}
input, textarea, select {
    background:#d3e3ff
}                        
.post-moderated, .post-redacted {
    background-color:#dde1ff
}
.post-moderated, .post-redacted {
    color:#000
}
a {
    color:#009f78
}                                            
}
