css:
@-moz-document url-prefix("https://opalium.dev/") {
header {
    background-color:#ffc6c6
}
body {
    background-color:#ffe1e1
}
.post-condensed, .post, .box {
    background-color:#efc7c7
}
.post-condensed, .post, .box {
    color:#000
}
.post-attribution, .box-attribution {
    color:#000
}
.box h3 {
    background-color:#ffc6c6
}
.btn {
    background-color:#ffc6c6
}
.post-hidden {
    background-color:#ffc6c6
}
.post-removed, .post-banned {
    background-color:#ffc6c6
}
input, textarea, select {
    background:#ffd3d3
}
.post-moderated, .post-redacted {
    background-color:#ffdddd
}
.post-moderated, .post-redacted {
    color:#000
}
a {
    color:#009f78
}
input, textarea, select {
    color:#000
}                                         
}
