css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ikhaya.ubuntuusers.de")
{

.article {
background-image: none !important;
}

p.meta {
background-color: #E6DCC6 !important;
}

h2, h3 {
border-bottom: 0 !important;

}
}

@-moz-document domain("ubuntuusers.de")
{

body {
font-family: Ubuntu,'DejaVu Sans','Bitstream Vera Sans', Verdana, sans-serif !important;
}

.appheader h1, .appheader h2, .appheader a, .category {
font-size: 1em !important;
}

div.pagination span.active {
background-color: #615D53 !important;
color: white !important;
}

div.pagination .pageselect, div.pagination .next, div.pagination .prev {
border:1px solid #615D53 !important;
font-size: 14px !important;
}

div.pagination a:hover {
background-color: #615D53 !important;
color: white !important;
}

div.pagination a {
font-weight: normal !important;
text-decoration: none !important;
text-shadow: none !important;
}

.wrap, .wrap2, .wrap3, .wrap4, .wrap5 {
background-image: none !important;
height: 0px !important;
}

.head th, .category {
background-image: none !important;
background-color: #DDDDDD !important;
}

.header {
background-image: none !important;
background-color: #B3AB9A !important;
height: 80px !important;
padding: 20px !important;
}

.topic_box h2 {
font-family: Ubuntu,'DejaVu Sans','Bitstream Vera Sans', Verdana, sans-serif !important;
background-color: #DDDDDD !important;
}

.text, .text blockquote {
color: #222222;
font-size: 1em;
}

.text pre {
font-size: 0.9em !important;
border-color: #B3AB9A !important;
}

.text blockquote {
font-weight: bold !important;
}

.pathbar .prev:hover, .pathbar .next:hover {
background-color: #D6CDB8 !important;
}

.pathbar {
border-color: #87806F !important;
}

.dropdown a, .trace a {
color: #171613 !important;
text-shadow: #87806F 1px 1px 1px !important;
font-size: 0.9em;
font-weight: bold !important;
}

.dropdown, .pathbar {
background-color: #615D53 !important;
}

.topic_title, .forum {
font-size: 1.0em !important;
}
.description, .date, .note, .note a, .trace a {
font-size: 0.9em !important;
}

a, :link, :visited {
color: #111111 !important;
font-weight: bold !important;
text-decoration: none !important;
text-shadow: #DEDEDE 1px 1px 2px !important;
}

a:hover, .text a {
text-decoration: underline !important;
}

.content .post .text :visited, .content #page :visited, .content .article .intro :visited, .content .article .text :visited, .prvmsg_body :visited, .preview :visited {
color: #440044 !important;
}

input.linkbutton, input.linkbutton:active {
border-color:#F8F8F8 #F8F8F8 #000088 !important;
color:#000088 !important;
}

.topic_box h2 {
background-image: none !important;
}

table tr.head th, table thead th {
color:#000000 !important;
}

.tabbar, .tabbar li.active {
background-image: none !important;
background-color: #B3AB9A;
}

.tabbar li:hover, .tabbar li.active:hover {
background-image: none !important;
background-color: #D6CDB8;
}

.tabbar a, .footer a, .appheader h1, .appheader h2, .appheader a  {
color: #47443D !important;
text-shadow: #C9C2B3 1px 1px 1px !important;
}

}
